import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const Page4 = () => {
  const predefinedData = Array.from({ length: 30 }, () => Math.floor(Math.random() * 49) + 30);

  const [data, setData] = useState(predefinedData);
  const [chartData, setChartData] = useState(null);
  const [intervalId, setIntervalId] = useState(null);

  const startChart = () => {
    if (intervalId) return;

    const id = setInterval(() => {
      setData((prevData) => {
        const newData = [...prevData, predefinedData[prevData.length % predefinedData.length]].slice(-30);
        updateChartData(newData);
        return newData;
      });
    }, 1000);
    setIntervalId(id);
  };

  const updateChartData = (dataArray) => {
    const labels = Array.from({ length: dataArray.length }, (_, i) => i + 1);
    setChartData({
      labels,
      datasets: [
        {
          label: 'Styled Data',
          data: dataArray,
          borderColor: '#4B0082', // Indigo color for line
          backgroundColor: 'rgba(75, 0, 130, 0.5)', // Semi-transparent fill
          borderWidth: 2, // Thicker line
          pointBackgroundColor: '#FF4500', // Orange points
          pointRadius: 5, // Larger points
          fill: true, // Fill the area under the line
          tension: 0.4, // Smoother curve
        },
      ],
    });
  };

  const handleClick = () => {
    setData(predefinedData);
    updateChartData(predefinedData);
    startChart();
  };

  useEffect(() => {
    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [intervalId]);

  return (
    <div>
      <button onClick={handleClick}>Start Chart</button>
      {chartData && (
        <Line
          data={chartData}
          options={{
            scales: {
              x: {
                title: {
                  display: true,
                  text: 'Time',
                },
                min: 0,
                max: 30,
                offset: true,
                grid: {
                  display: false, // Hide grid lines on x-axis
                },
              },
              y: {
                title: {
                  display: true,
                  text: 'Value',
                },
                min: 30,
                max: 80,
                grid: {
                  color: '#E0E0E0', // Light gray grid lines
                },
              },
            },
            layout: {
              padding: {
                left: '50%', // Push graph to the right
                right: 100,
              },
            },
            plugins: {
              legend: {
                display: true,
                labels: {
                  color: '#4B0082', // Indigo color for legend text
                },
              },
              tooltip: {
                backgroundColor: '#FFD700', // Gold color for tooltips
                titleColor: '#000000', // Black text
              },
            },
            elements: {
              line: {
                borderDash: [5, 5], // Dashed line
              },
            },
          }}
          width={600}
          height={300}
        />
      )}
    </div>
  );
};

export default Page4;
