import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [previousValue, setPreviousValue] = useState(0);
  const [currentValue, setCurrentValue] = useState(60600);
  const [baseValue, setBaseValue] = useState(0);
  const [rectangles, setRectangles] = useState([]);
  const [centerValue, setCenterValue] = useState(0);
  const [range, setRange] = useState(120);
  const canvasRef = useRef(null);

  const fetchBitcoinPrice = async () => {
    try {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
      const data = await response.json();
      const price = data.bpi.USD.rate_float;
      return Math.round(price);
    } catch (error) {
      console.error('Ошибка при получении цены биткоина:', error);
      return currentValue;
    }
  };

  const getRandomPriceChange = () => {
    let currentSeconds = new Date().getSeconds();

    if (currentSeconds === 0) {
      currentSeconds = 10;
    } else if (currentSeconds < 10) {
      currentSeconds *= 7;
    }

    return currentSeconds % 2 === 0 ? currentSeconds : -currentSeconds;
  };

  const updateTargetValue = async () => {
    const currentPrice = await fetchBitcoinPrice();
    const priceChange = getRandomPriceChange();
    setCurrentValue(currentPrice + priceChange);
  };

  useEffect(() => {
    const updatePrice = async () => {
      const initialPrice = await fetchBitcoinPrice();
      setCurrentValue(initialPrice);
      setBaseValue(initialPrice);
      setCenterValue(initialPrice);
      setPreviousValue(initialPrice);
    };

    updatePrice();
  }, []);

  useEffect(() => {
    if (currentValue !== previousValue) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      const scale = window.devicePixelRatio || 1;
      canvas.width = 500 * scale;
      canvas.height = 300 * scale;
      ctx.scale(scale, scale);

      const difference = currentValue - previousValue;
      const step = difference > 0 ? 1 : -1;
      let progress = 0;

      const startY = 150 - ((previousValue - centerValue + range / 2) / range) * 300;
      const targetY = 150 - ((currentValue - centerValue + range / 2) / range) * 300;
      const rectWidth = 30;

      const interval = setInterval(() => {
        progress += step;

        ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

        const updatedRectangles = rectangles.map((rect) => ({
          ...rect,
          x: rect.x - rectWidth,
        }));

        updatedRectangles.forEach((rect) => {
          ctx.fillStyle = rect.color;
          ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
        });

        const height = Math.abs(progress);
        const rectY = difference > 0 ? startY - height : startY;
        ctx.fillStyle = difference > 0 ? 'green' : 'red';
        ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

        if (Math.abs(progress) >= Math.abs(targetY - startY)) {
          clearInterval(interval);

          updatedRectangles.push({
            x: canvas.width / scale / 2 - 15,
            y: rectY,
            width: rectWidth,
            height: height,
            color: difference > 0 ? 'green' : 'red',
          });

          setRectangles(updatedRectangles);
          setPreviousValue(currentValue);
          updateTargetValue(); // Запуск следующей анимации после завершения текущей
        }
      }, 20);

      return () => clearInterval(interval);
    }
  }, [currentValue, previousValue, rectangles]);

  const handleFixValue = () => {
    setCenterValue(currentValue);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
