import React, { useEffect, useState } from 'react';
import { Chart } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, CandlestickController, TimeScale, Tooltip } from 'chart.js';
import 'chartjs-chart-financial';

// Регистрация необходимых компонентов Chart.js
ChartJS.register(CategoryScale, LinearScale, CandlestickController, TimeScale, Tooltip);

const Page4 = () => {
  const [data, setData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Candlestick Data',
        data: [],  // Данные для свечей
        borderColor: 'rgba(75, 192, 192, 1)',
      },
    ],
  });

  // Генерация случайной свечи
  const generateRandomCandle = (prevClose) => {
    const open = prevClose;
    const close = open + (Math.random() - 0.5) * 10;
    const high = Math.max(open, close) + Math.random() * 5;
    const low = Math.min(open, close) - Math.random() * 5;

    return {
      x: new Date().toLocaleTimeString(),
      o: open,
      h: high,
      l: low,
      c: close,
    };
  };

  // Добавление новой свечи
  const addCandle = () => {
    const newData = [...data.datasets[0].data];
    const newLabels = [...data.labels];

    if (newData.length >= 20) {  // Ограничение до 20 свечей
      newData.shift();
      newLabels.shift();
    }

    const lastCandle = newData.length ? newData[newData.length - 1] : { c: 50 };
    const newCandle = generateRandomCandle(lastCandle.c);

    newData.push(newCandle);
    newLabels.push(newCandle.x);

    setData({
      labels: newLabels,
      datasets: [{
        ...data.datasets[0],
        data: newData
      }]
    });
  };

  // Обновление данных каждую секунду
  useEffect(() => {
    const interval = setInterval(addCandle, 1000);
    return () => clearInterval(interval);
  }, [data]);

  // Инлайн стили для контейнера графика
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f5f5f5'
  };

  const chartStyle = {
    width: '80%',
    height: '80%',
  };

  return (
    <div style={containerStyle}>
      <div style={chartStyle}>
        <Chart
          type="candlestick"
          data={data}
          options={{
            maintainAspectRatio: false,
            scales: {
              x: {
                type: 'time',
                time: {
                  unit: 'second',
                },
              },
              y: {
                beginAtZero: false,
              },
            },
          }}
        />
      </div>
    </div>
  );
};

export default Page4;
