import React, { useEffect, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import { EffectCoverflow } from 'swiper/modules/effect-coverflow'; // Правильный импорт эффекта
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [cards, setCards] = useState([
    { id: 1, content: 'Card 1' },
    { id: 2, content: 'Card 2' },
    { id: 3, content: 'Card 3' },
  ]);

  // Анимация на загрузку
  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const addCard = () => {
    const newCard = { id: cards.length + 1, content: `Card ${cards.length + 1}` };
    setCards([...cards, newCard]);
  };

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <div className="level-stars"></div>
        <h1>1 Level</h1>
        <button onClick={addCard}> Add Card </button>
      </div>
      <div className="level-content">
        <Swiper
          effect="coverflow"
          grabCursor={true}
          centeredSlides={true}
          slidesPerView={1}
          coverflowEffect={{
            rotate: 50,
            stretch: 0,
            depth: 200,
            modifier: 1,
            slideShadows: true,
          }}
          loop={true}
          modules={[EffectCoverflow]} // Подключаем модуль эффекта
        >
          {cards.map(card => (
            <SwiperSlide key={card.id}>
              <div className="card">{card.content}</div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

export default LevelPage;
