import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [elementsVisible, setElementsVisible] = useState(false);
  const [level1, setLevel1] = useState(1);
  const [progress1, setProgress1] = useState(0); // Прогресс в процентах
  const [balance, setBalance] = useState(0); // Баланс пользователя
  const [balanceRate1, setBalanceRate1] = useState(1); // Текущий "рейт" баланса
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();

  const leftTexts = ['Left Text 1', 'Left Text 2', 'Left Text 3'];
  const rightTexts = ['Right Text 1', 'Right Text 2', 'Right Text 3'];

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        setIsExiting(true);
        setTimeout(() => {
          navigate('/');
        }, 850);
      });
    }
    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    // Получаем баланс и баланс рейт из CloudStorage
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate1', 'progress1', 'level1'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialBalanceRate = result.balanceRate1 ? parseInt(result.balanceRate1, 10) : 1;
          const initialProgress = result.progress1 ? parseInt(result.progress1, 10) : 0;
          const initialLevel = result.level1 ? parseInt(result.level1, 10) : 1;

          setBalance(initialBalance);
          setBalanceRate1(initialBalanceRate);
          setProgress1(initialProgress);
          setLevel1(initialLevel);
        }
      });
    };

    getInitialData();
  }, []);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);

    setTimeout(() => {
      setElementsVisible(true);
    }, 550);
  }, []);

  const handleBlockClick = () => {
    // Пороговые значения для баланса
    const balanceThresholds = [1, 5, 10, 20, 40, 80, 100, 1000, 5000, 10000, 11000, 12000, 13000, 14000, 15000];
    const requiredBalance = balanceThresholds[balanceRate1 - 1] || 0;

    if (balance >= requiredBalance) {
      // Логика для увеличения прогресса
      const progressIncrements = {
        1: 20, // Баланс 1, рейт 1 -> +20% прогресса
        5: 20, // Баланс 5, рейт 2 -> +20% прогресса
        10: 20, // Баланс 10, рейт 3 -> +20% прогресса
        20: 20, // Баланс 20, рейт 3 -> +20% прогресса
        40: 20  // Баланс 40, рейт 5 -> +20% прогресса
      };

      const increment = progressIncrements[balance] || 0;
      const newProgress = progress1 + increment;

      if (newProgress >= 100) {
        alert('Level Up!');
        const newLevel = level1 + 1;
        setLevel1(newLevel);
        setProgress1(0);

        // Обновляем уровень и сбрасываем прогресс в CloudStorage
        window.Telegram.WebApp.CloudStorage.setItem('level1', newLevel.toString(), (error) => {
          if (error) console.error('Failed to update level in cloud storage:', error);
        });
        window.Telegram.WebApp.CloudStorage.setItem('progress1', '0', (error) => {
          if (error) console.error('Failed to reset progress in cloud storage:', error);
        });
      } else {
        setProgress1(newProgress);

        // Обновляем прогресс в CloudStorage
        window.Telegram.WebApp.CloudStorage.setItem('progress1', newProgress.toString(), (error) => {
          if (error) console.error('Failed to update progress in cloud storage:', error);
        });
      }

      // Обновляем баланс рейт
      const newBalanceRate = balanceRate1 + 1;
      setBalanceRate1(newBalanceRate);
      window.Telegram.WebApp.CloudStorage.setItem('balanceRate1', newBalanceRate.toString(), (error) => {
        if (error) console.error('Failed to update balanceRate in cloud storage:', error);
      });
    } else {
      alert(`Not enough balance! You need at least ${requiredBalance} to proceed.`);
    }
  };

  const getProgressColor = () => {
    const colors = ['#4caf50', '#2196f3', '#ff9800', '#9c27b0', '#e91e63'];
    return colors[(level1 - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      {/* Верхняя часть */}
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          {level1} LEVEL
        </h1>

        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          margin: '10px auto',
          backgroundColor: 'rgba(221, 221, 221, 0.1)',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          <div style={{
            height: '100%',
            width: `${progress1}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Блоки */}
        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          cursor: (balance >= 1) ? 'pointer' : 'not-allowed',
          opacity: (balance >= 1) ? 1 : 0.5,
        }} onClick={handleBlockClick}>
          <span>{leftTexts[0]}</span>
          <span>{rightTexts[0]}</span>
        </div>

        {/* Блоки для будущих условий */}
        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: 0.5, // Сделаем их неактивными пока
        }}>
          <span>{leftTexts[1]}</span>
          <span>{rightTexts[1]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: 0.5, // Сделаем их неактивными пока
        }}>
          <span>{leftTexts[2]}</span>
          <span>{rightTexts[2]}</span>
        </div>
      </div>

      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
