import React, { useState, useEffect } from 'react';

const Page4 = () => {
  const [balance, setBalance] = useState(0);
  const [btcPrice, setBtcPrice] = useState(null);

  useEffect(() => {
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          setBalance(initialBalance);
        }
      });
    };

    getInitialData();
  }, []);

  useEffect(() => {
    const fetchBTCPrice = () => {
      fetch('https://api.binance.com/api/v3/ticker/price?symbol=BTCUSDT')
        .then(response => response.json())
        .then(data => {
          setBtcPrice(data.price);
        })
        .catch(error => {
          console.error('Failed to fetch BTC price:', error);
        });
    };

    fetchBTCPrice();
    const interval = setInterval(fetchBTCPrice, 10000); // Обновляем курс каждые 10 секунд

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ position: 'relative', padding: '20px' }}>
      {/* Баланс */}
      <div style={{
        position: 'absolute',
        top: '10px',
        right: '10px',
        display: 'flex',
        alignItems: 'center',
        fontSize: '1.2em'
      }}>
        <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance" style={{ width: '20px', marginRight: '5px' }} />
        {balance.toLocaleString()}
      </div>

      {/* Курс BTC/USDT */}
      <div style={{
        marginTop: '50px',
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        textAlign: 'center',
        fontSize: '1.5em'
      }}>
        {btcPrice ? (
          <div>Current BTC/USDT Price: {parseFloat(btcPrice).toFixed(2)} USD</div>
        ) : (
          <div>Loading BTC/USDT price...</div>
        )}
      </div>
    </div>
  );
};

export default Page4;
