import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const canvasRef = useRef(null);
    const [isRunning, setIsRunning] = useState(false);
    const [height, setHeight] = useState(0);
    const [targetHeight, setTargetHeight] = useState(0);
    const [direction, setDirection] = useState(0); // 1 for up, -1 for down
    const [rectangles, setRectangles] = useState([]);

    const canvasWidth = 600;  // Set the width of the canvas
    const canvasHeight = 400; // Set the height of the canvas
    const rectWidth = 30;     // Width of each rectangle
    const centerY = canvasHeight / 2;  // The center of the canvas

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvasWidth, canvasHeight);  // Clear the canvas

        // Draw all rectangles
        rectangles.forEach((rect, index) => {
            ctx.beginPath();
            ctx.rect(centerY - rect.yOffset, rect.xOffset, rectWidth, rect.height);
            ctx.strokeStyle = rect.direction === 1 ? 'green' : 'red';
            ctx.stroke();
            ctx.closePath();
        });
    }, [rectangles]);

    useEffect(() => {
        if (!isRunning) return;

        const interval = setInterval(() => {
            const newTarget = Math.floor(Math.random() * 41) * (Math.random() > 0.5 ? 1 : -1);
            setTargetHeight(newTarget);
            setDirection(newTarget > 0 ? 1 : -1);
        }, 10000);

        return () => clearInterval(interval);
    }, [isRunning]);

    useEffect(() => {
        if (!isRunning) return;

        const stepHeight = targetHeight / 100; // 10 seconds, so 100 steps

        const interval = setInterval(() => {
            setHeight(prevHeight => {
                const nextHeight = prevHeight + stepHeight;

                if ((stepHeight > 0 && nextHeight >= targetHeight) || (stepHeight < 0 && nextHeight <= targetHeight)) {
                    clearInterval(interval);
                    setRectangles(prevRectangles => [
                        ...prevRectangles,
                        { height: targetHeight, direction, xOffset: (prevRectangles.length + 1) * -rectWidth, yOffset: centerY - targetHeight }
                    ]);
                    setHeight(0);
                }

                return nextHeight;
            });
        }, 100); // 100 ms for smooth animation

        return () => clearInterval(interval);
    }, [targetHeight, isRunning]);

    const handleStartStop = () => {
        setIsRunning(prevState => !prevState);
    };

    return (
        <div>
            <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} style={{ border: '1px solid black' }} />
            <button onClick={handleStartStop}>
                {isRunning ? 'Stop' : 'Start'}
            </button>
        </div>
    );
};

export default Page4;
