import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ConfettiCanvas from './ConfettiCanvas';


const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [elementsVisible, setElementsVisible] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0);
  const [balance, setBalance] = useState(0);
  const [balanceRate, setBalanceRate] = useState(1);
  const [usersRate, setUsersRate] = useState(1);
  const [usersCount, setUsersCount] = useState(0);
  const [Ad, setAd] = useState(0);
  const [isExiting, setIsExiting] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [shake, setShake] = useState(false);
  const [shake1, setShake1] = useState(false);

  const navigate = useNavigate();

  const balanceThresholds = [1000, 5000, 10000, 50000, 100000, 250000, 500000, 1000000, 2000000, 5000000, 10000000, 50000000, 100000000, 250000000, 500000000, 1000000000, 100000000000];
  const usersThresholds = [1, 2, 5, 7, 10, 15, , 20, 30, 50, 75, 100, 150, 250, 500, 750, 1000, 2000, 5000, 10000];
  const levelThresholds = [10000, 50000, 100000, 250000, 500000, 1000000, 2000000, 5000000, 10000000, 50000000];

  const triggerHapticFeedback = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.selectionChanged('light');
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  const triggerHapticFeedbackError = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.notificationOccurred("error");
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  const triggerHapticFeedbackSuccess = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.notificationOccurred("success");
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };


  useEffect(() => {
    // Получаем данные из cloudStorage при загрузке страницы
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate001', 'level001', 'progress001', 'usersCount', 'usersRate001', 'AdTapDuck001'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialRate = result.balanceRate001 ? parseInt(result.balanceRate001, 10) : 1;
          const initialUsersRate = result.usersRate001 ? parseInt(result.usersRate001, 10) : 1;
          const initialLevel = result.level001 ? parseInt(result.level001, 10) : 1;
          const initialProgress = result.progress001 ? parseInt(result.progress001, 10) : 0;
          const initialUsers = result.usersCount ? parseInt(result.usersCount, 10) : 0;
          const initialAd = result.AdTapDuck001 ? parseInt(result.AdTapDuck001, 10) : 0;

          setBalance(initialBalance);
          setBalanceRate(initialRate);
          setLevel(initialLevel);
          setProgress(initialProgress);
          setUsersCount(initialUsers)
          setUsersRate(initialUsersRate)
          setAd(initialAd)
        }
      });
    };

    getInitialData();
  }, []);

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        setIsExiting(true);
        setTimeout(() => {
          navigate('/');
        }, 850);
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
    setTimeout(() => {
      setElementsVisible(true);
    }, 550);
  }, []);

  const redirectToChannel = () => {
    if (Ad === 1) {
      triggerHapticFeedbackSuccess()

      if (progress >= 80) {
        setProgress(prev => prev + 20);
        setTimeout(() => {
          setLevel(prev => prev + 1);
          setProgress(0);
          setAd(prev => prev + 1)
          setShowConfetti(true);
          window.Telegram.WebApp.CloudStorage.setItem('balance', Math.floor(balance + levelThresholds[level - 1]).toString(), (error) => {
            if (error) {
              console.error('Failed to update balance in cloud storage:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('progress001', (0).toString(), (error, success) => {
            if (error) {
              console.error('Error setting progress:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('level001', (level + 1).toString(), (error, success) => {
            if (error) {
              console.error('Error setting level:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('AdTapDuck001', (Ad + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });

        }, 500);

      } else {

        setProgress(prev => prev + 20);
        setAd(prev => prev + 1)

        window.Telegram.WebApp.CloudStorage.setItem('progress001', (progress + 20).toString(), (error, success) => {
          if (error) {
            console.error('Error setting progress:', error);
          }
        });
          window.Telegram.WebApp.CloudStorage.setItem('AdTapDuck001', (Ad + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
      }
    } else {
      triggerHapticFeedback()

      if (Ad <= 0) {
        setAd(prev => prev + 1)
        window.open('https://t.me/tap_duck_official', '_blank');
      } else {
        window.open('https://t.me/tap_duck_official', '_blank');
      }
    }

  };

  const handleIncreaseProgressBalance = () => {
    if (balance >= balanceThresholds[balanceRate - 1]) {
      triggerHapticFeedbackSuccess()
      if (progress >= 80) {
        setProgress(prev => prev + 20);
        setTimeout(() => {
          setLevel(prev => prev + 1);
          setProgress(0);
          setShowConfetti(true);
          window.Telegram.WebApp.CloudStorage.setItem('balance', Math.floor(balance + levelThresholds[level - 1]).toString(), (error) => {
            if (error) {
              console.error('Failed to update balance in cloud storage:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('progress001', (0).toString(), (error, success) => {
            if (error) {
              console.error('Error setting progress:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('level001', (level + 1).toString(), (error, success) => {
            if (error) {
              console.error('Error setting level:', error);
            }
          });
          setBalanceRate(prev => prev + 1);
          window.Telegram.WebApp.CloudStorage.setItem('balanceRate001', (balanceRate + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
        }, 500);

      } else {
        setProgress(prev => prev + 20);
        window.Telegram.WebApp.CloudStorage.setItem('progress001', (progress + 20).toString(), (error, success) => {
          if (error) {
            console.error('Error setting progress:', error);
          }
        });
        setBalanceRate(prev => prev + 1);
          window.Telegram.WebApp.CloudStorage.setItem('balanceRate001', (balanceRate + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
      }
    } else {
      triggerHapticFeedbackError()
      setShake1(true); // Активируем тряску
      setTimeout(() => setShake1(false), 500); 
    }
  };

  const handleIncreaseProgressUsers = () => {
    if (usersCount >= usersThresholds[usersRate - 1]) {
      triggerHapticFeedbackSuccess()
      if (progress >= 80) {
        setProgress(prev => prev + 20);
        setTimeout(() => {
          setLevel(prev => prev + 1);
          setProgress(0);
          setShowConfetti(true);
          window.Telegram.WebApp.CloudStorage.setItem('balance', Math.floor(balance + levelThresholds[level - 1]).toString(), (error) => {
            if (error) {
              console.error('Failed to update balance in cloud storage:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('progress001', (0).toString(), (error, success) => {
            if (error) {
              console.error('Error setting progress:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('level001', (level + 1).toString(), (error, success) => {
            if (error) {
              console.error('Error setting level:', error);
            }
          });
          setUsersRate(prev => prev + 1);
          window.Telegram.WebApp.CloudStorage.setItem('usersRate001', (usersRate + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
        }, 500);

      } else {
        setProgress(prev => prev + 20);
        window.Telegram.WebApp.CloudStorage.setItem('progress001', (progress + 20).toString(), (error, success) => {
          if (error) {
            console.error('Error setting progress:', error);
          }
        });
        setUsersRate(prev => prev + 1);
          window.Telegram.WebApp.CloudStorage.setItem('usersRate001', (usersRate + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
      }
    } else {
      triggerHapticFeedbackError()
      setShake(true); // Активируем тряску
      setTimeout(() => setShake(false), 500); 
    }
  };

  const getProgressColor = () => {
    const colors = [
      '#edfd1d', 
      '#5bf92e',  
      '#00fdff',  
      '#6582ff',  
      '#945fff',  
      '#fe0c7f',  // Бронзовый
      '#fd0101',  // Серебристый
      '#fd6012',  // Золотой
      '#fdb71e',  // Платиновый
      '#fde330'   // Бриллиантовый
    ];

    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      <div className={`balance-display ${'slide-in-right'}`} style={{opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'}}>
                <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance Icon" className="balance-icon" />
                {balance.toLocaleString()}
            </div>
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>


        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          {level} LEVEL
        </h1>

        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          marginBottom: '50px',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out',
          boxShadow: `0 0 15px 5px ${getProgressColor()}80`,  // Начальная тень
          animation: 'pulse 2.5s infinite'  // Анимация
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.05)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>
        {showConfetti && <ConfettiCanvas trigger={showConfetti} />}

        <div style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '17px',
          zIndex: '2',
          marginBottom: '10px',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}> Next Level Reward </div>
        <div style={{
          color: '#fff',
          fontSize: '17px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}> +{levelThresholds[level - 1].toLocaleString()} <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance Icon" style={{height: '17px', marginBottom: '-2px'}} /></div>

        {/* Независимый блок с обработчиком клика для увеличения прогресса */}
        <div onClick={handleIncreaseProgressBalance} className={shake1 ? 'shake1' : ''} style={{
          marginTop: "20px",
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          cursor: 'pointer',
          fontSize: '14px',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s',
          backgroundColor: 'rgba(0, 0, 0, 0.1)', 

        }}>
          <span style={{color: shake1 ? 'red' : 'white'}}>Reach {balanceThresholds[balanceRate - 1].toLocaleString()} Coins</span>
          <span style={{color: getProgressColor()}}>+20Xp</span>
        </div>

        <div 
          onClick={handleIncreaseProgressUsers} 
          className={shake ? 'shake' : ''}
          style={{
            marginTop: "20px",
            borderRadius: '25px',
            height: '50px',
            width: '70%',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '0 20px',
            zIndex: '2',
            fontSize: '14px',
            opacity: elementsVisible && !isExiting ? 1 : 0,
            transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s',
            backgroundColor: 'rgba(0, 0, 0, 0.1)',
          }}
        >
          <span style={{color: shake ? 'red' : 'white'}}>Invite {usersThresholds[usersRate - 1].toLocaleString()} {usersThresholds[usersRate - 1] > 1 ? 'friends' : 'friend'}</span>
          <span style={{color: getProgressColor()}}>+20Xp</span>
        </div>


        <div onClick={redirectToChannel} style={{
          marginTop: "20px",
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          fontSize: '14px',
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s',
          backgroundColor: 'rgba(0, 0, 0, 0.1)', 

        }}>
          <span>Subscribe on Duck Community</span>
          <span style={{color: getProgressColor()}}>+20Xp</span>
        </div>

      </div>

      <style>{`
      .shake {
      animation: shake-animation 0.5s ease;
    }

    @keyframes shake-animation {
      0% { transform: translate(0, 0); }
      10% { transform: translate(-5px, 0); }
      20% { transform: translate(5px, 0); }
      30% { transform: translate(-5px, 0); }
      40% { transform: translate(5px, 0); }
      50% { transform: translate(-5px, 0); }
      60% { transform: translate(5px, 0); }
      70% { transform: translate(-5px, 0); }
      80% { transform: translate(5px, 0); }
      90% { transform: translate(-5px, 0); }
      100% { transform: translate(0, 0); }
    }
      .shake1 {
      animation: shake1-animation 0.5s ease;
    }

    @keyframes shake1-animation {
      0% { transform: translate(0, 0); }
      10% { transform: translate(-5px, 0); }
      20% { transform: translate(5px, 0); }
      30% { transform: translate(-5px, 0); }
      40% { transform: translate(5px, 0); }
      50% { transform: translate(-5px, 0); }
      60% { transform: translate(5px, 0); }
      70% { transform: translate(-5px, 0); }
      80% { transform: translate(5px, 0); }
      90% { transform: translate(-5px, 0); }
      100% { transform: translate(0, 0); }
    }
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
          @keyframes pulse {
      0% {
        box-shadow: 0 0 10px 1px ${getProgressColor()}80;  // Начальная тень
      }
      50% {
        box-shadow: 0 0 10px 1px ${getProgressColor()}10;  // Светлая тень
      }
      100% {
        box-shadow: 0 0 10px 1px ${getProgressColor()}80;  // Обратно к начальной
      }
    }
      `}</style>
    </div>
  );
};

export default LevelPage;
