import React, { useEffect, useRef } from 'react';
import { createChart, CrosshairMode } from 'lightweight-charts';

const Page4 = () => {
    const chartContainerRef = useRef();
    const chartRef = useRef();
    const candlestickSeriesRef = useRef();

    useEffect(() => {
    candlestickSeriesRef.current.createPriceLine({
        price: lastClose,
        color: 'rgba(255, 0, 0, 0.8)',
        lineWidth: 2,
        lineStyle: 2, // Пунктирная линия
        axisLabelVisible: true,
        title: 'Current Price',
    });
}, [lastClose]);

    useEffect(() => {
        // Создаем график
        chartRef.current = createChart(chartContainerRef.current, {
            width: chartContainerRef.current.clientWidth,
            height: 500,
            layout: {
                backgroundColor: '#ffffff',
                textColor: '#000',
            },
            grid: {
                vertLines: {
                    color: '#eeeeee',
                },
                horzLines: {
                    color: '#eeeeee',
                },
            },
            crosshair: {
                mode: CrosshairMode.Normal,
            },
            priceScale: {
                borderColor: '#cccccc',
            },
            timeScale: {
                borderColor: '#cccccc',
                timeVisible: true,
                secondsVisible: true,
            },
        });

        // Создаем серию свечей
        candlestickSeriesRef.current = chartRef.current.addCandlestickSeries({
            upColor: '#4caf50',
            downColor: '#f44336',
            borderDownColor: '#f44336',
            borderUpColor: '#4caf50',
            wickDownColor: '#f44336',
            wickUpColor: '#4caf50',
        });

        // Функция генерации случайных данных свечей
        const generateRandomCandle = (lastClose) => {
            const open = lastClose;
            const close = open + (Math.random() - 0.5) * 2;
            const high = Math.max(open, close) + Math.random() * 1;
            const low = Math.min(open, close) - Math.random() * 1;
            return { open, high, low, close };
        };

        // Начальные данные
        let currentTime = Math.floor(Date.now() / 1000);
        let lastClose = 50000; // Начальная цена
        const initialCandles = [];
        for (let i = 0; i < 50; i++) {
            const candle = generateRandomCandle(lastClose);
            lastClose = candle.close;
            initialCandles.push({
                time: currentTime,
                ...candle,
            });
            currentTime += 5;
        }

        candlestickSeriesRef.current.setData(initialCandles);

        // Обновляем данные каждые 5 секунд
        const interval = setInterval(() => {
            const candle = generateRandomCandle(lastClose);
            lastClose = candle.close;
            currentTime += 5;
            candlestickSeriesRef.current.update({
                time: currentTime,
                ...candle,
            });
        }, 5000);

        // Удаляем интервал при размонтировании компонента
        return () => clearInterval(interval);
    }, []);

    // Отслеживание размера окна
    useEffect(() => {
        const handleResize = () => {
            chartRef.current.applyOptions({ width: chartContainerRef.current.clientWidth });
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return <div ref={chartContainerRef} />;
};

export default Page4;
