import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const values = [
    78, 230, 230, 231, 2, 110, 67, 84, 56, 90, 102, 48, 88, 61, 70, 109, 81, 52, 94, 66, 75, 118, 118,
    // другие значения...
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentValue, setCurrentValue] = useState(values[0]);
  const [dataPoints, setDataPoints] = useState([]);
  const [fixedValue, setFixedValue] = useState(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    if (currentIndex < values.length) {
      const targetValue = values[currentIndex];
      const difference = Math.abs(targetValue - currentValue);

      const intervalDuration = 1500 / difference;

      const interval = setInterval(() => {
        setCurrentValue(prevValue => {
          if (prevValue < targetValue) {
            setFixedValue(prevValue + 1);
            return prevValue + 1;
          } else if (prevValue > targetValue) {
            setFixedValue(prevValue - 1);
            return prevValue - 1;
          } else {
            setDataPoints(prev => [...prev, prevValue]);
            setCurrentIndex(currentIndex + 1);
            setFixedValue(prevValue);
            return prevValue;
          }
        });
      }, intervalDuration);

      return () => clearInterval(interval);
    }
  }, [currentIndex, currentValue, values]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Масштабируем холст для HiDPI экранов
    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 300 * scale;
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      ctx.strokeStyle = fixedValue !== null ? 'lightblue' : '#FFD700';
      ctx.lineWidth = 1.5;
      ctx.beginPath();

      dataPoints.forEach((value, index) => {
        const x = (canvas.width / scale / 2) + index * 10 - dataPoints.length * 10;
        const y = canvas.height / scale - (value / 1000) * canvas.height / scale;
        if (index === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      if (fixedValue !== null) {
        const fixedY = canvas.height / scale - (fixedValue / 1000) * canvas.height / scale;
        ctx.strokeStyle = 'lightblue';
        ctx.lineWidth = 1;
        ctx.beginPath();
        ctx.moveTo(0, fixedY);
        ctx.lineTo(canvas.width / scale, fixedY);
        ctx.stroke();

        // Выводим текущее значение рядом с фиксированной линией
        ctx.font = '12px Arial';
        ctx.fillStyle = 'lightblue';
        ctx.fillText(`${currentValue}`, canvas.width / scale - 150, fixedY - 10);
      }
    }
  }, [dataPoints, fixedValue, currentValue]);

  const handleFixValue = () => {
    setFixedValue(currentValue);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
