import React, { useState, useEffect } from 'react';
import './Stories.css';

const storiesData = [
  { id: 1, content: 'Сторис 1', img: 'https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png' },
  { id: 2, content: 'Сторис 2', img: 'https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png' },
  { id: 3, content: 'Сторис 3', img: 'https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png' },
];

const Stories = () => {
  const [currentStory, setCurrentStory] = useState(0);

  useEffect(() => {
    window.Telegram.WebApp.CloudStorage.getItems(['storiesViewed'], (error, result) => {
      if (error) {
        console.error('Failed to get storiesViewed from cloud storage:', error);
      } else {
        const storiesViewed = result.storiesViewed ? result.storiesViewed === 'true' : false;
        if (storiesViewed) {
          setCurrentStory(-1); // Не показывать сторис
        }
      }
    });
  }, []);

  const handleNextStory = () => {
    if (currentStory < storiesData.length - 1) {
      setCurrentStory(currentStory + 1);
    } else {
      window.Telegram.WebApp.CloudStorage.setItem('storiesViewed', 'true', (error) => {
        if (error) {
          console.error('Failed to set storiesViewed in cloud storage:', error);
        }
      });
      setCurrentStory(-1); // Больше не показывать сторис
    }
  };

  const handlePreviousStory = () => {
    if (currentStory > 0) {
      setCurrentStory(currentStory - 1);
    }
  };

  if (currentStory === -1) {
    return null; // Не показывать компонент, если сторис уже просмотрены
  }

  return (
    <div className="stories-container">
      <div className="story">
        <img src={storiesData[currentStory].img} alt={`Story ${currentStory + 1}`} />
        <p>{storiesData[currentStory].content}</p>
      </div>
      <div className="navigation">
        {currentStory > 0 && <button onClick={handlePreviousStory} className="nav-button">Previous</button>}
        {currentStory < storiesData.length - 1 && <button onClick={handleNextStory} className="nav-button">Next</button>}
      </div>
      <div className="pagination">
        {storiesData.map((_, index) => (
          <span key={index} className={`dot ${index === currentStory ? 'active' : ''}`}></span>
        ))}
      </div>
    </div>
  );
};

export default Stories;
