import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Page3.css';
import ConfettiCanvas from './ConfettiCanvas';
import LoadingScreen from './LoadingScreen';

function Page3() {
  const [value, setValue] = useState(0.00);
  const [balance, setBalance] = useState(() => {
    const savedBalance = localStorage.getItem('balance');
    return savedBalance ? parseFloat(savedBalance) : 0.00;
  });
  const [bet, setBet] = useState('');
  const [tickets, setTickets] = useState(() => {
    const savedTickets = localStorage.getItem('tickets');
    return savedTickets ? parseInt(savedTickets, 10) : 9;
  });
  const [message, setMessage] = useState('Введите ставку');
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [result, setResult] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [animateInput, setAnimateInput] = useState(false);
  const [animateValue, setAnimateValue] = useState(false);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState('');

  const intervalRef = useRef(null);
  const timeoutRef = useRef(null);

  const navigate = useNavigate(); // Хук для навигации

  const triggerHapticFeedback = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.impactOccurred('heavy');
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  const triggerHapticFeedbackSoft = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.impactOccurred('rigid');
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  useEffect(() => {
    const betAmount = parseFloat(bet);
    if (bet === '') {
      setMessage('Select your bet');
    } else if (isNaN(betAmount) || betAmount <= 0 || betAmount > balance) {
      setMessage('Not enough tokens');
    } else if (!tickets) {
      setMessage('Need more tickets');
    } else {
      setMessage('Hold on')
    }
  }, [bet, balance]);

  useEffect(() => {
    setAnimateInput(true);
    setAnimateValue(true);
  }, []);

  useEffect(() => {
    const checkTicketsReset = () => {
      const now = new Date();
      const lastReset = localStorage.getItem('lastReset');
      const lastResetDate = lastReset ? new Date(lastReset) : null;

      if (!lastResetDate || now.getDate() !== lastResetDate.getDate()) {
        setTickets(9);
        localStorage.setItem('tickets', '9');
        localStorage.setItem('lastReset', now.toString());
      }
    };

    checkTicketsReset();

    const interval = setInterval(checkTicketsReset, 60000); // Проверка каждую минуту

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const loadingTimeout = setTimeout(() => setLoading(false), 4000); // Hide loading screen after 4 seconds
    return () => clearTimeout(loadingTimeout);
  }, []);

  useEffect(() => {
    if (tickets === 0) {
      const calculateTimeLeft = () => {
        const now = new Date();
        const nextReset = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1);
        const difference = nextReset - now;

        let hours = Math.floor(difference / (1000 * 60 * 60));
        let minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
        let seconds = Math.floor((difference % (1000 * 60)) / 1000);

        setTimeLeft(`${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);
      };

      calculateTimeLeft();
      const timeInterval = setInterval(calculateTimeLeft, 1000);

      return () => clearInterval(timeInterval);
    }
  }, [tickets]);

  // Добавим обработку кнопки назад
  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        navigate('/page1'); // Переход на Page1 при нажатии на кнопку назад
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  const handle10 = (cef) => {
    const betValue = Math.floor(balance * cef);
    setBet(betValue);
  }

  const handleButtonPress = () => {
    const betAmount = parseFloat(bet);
    if (!isNaN(betAmount) && betAmount <= balance && betAmount > 0 && tickets > 0 && !isPlaying) {
      setIsPlaying(true);
      setValue(1.00);
      setGameOver(false);
      setResult(null);
      setShowConfetti(false);
      setTickets((prevTickets) => {
        const newTickets = prevTickets - 1;
        localStorage.setItem('tickets', newTickets.toString());
        return newTickets;
      });
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance - betAmount).toFixed(2));
        return isNaN(newBalance) || newBalance < 0 ? 0 : newBalance;
      });

      const maxMultiplier = 20.00;
      intervalRef.current = setInterval(() => {
        setValue((prevValue) => parseFloat((prevValue + 0.01).toFixed(2)));
        triggerHapticFeedback();
      }, 100); // Увеличиваем коэффициент каждые 100 мс

      const randomFactor = Math.random();
      let randomTime;

      if (randomFactor < 0.1) {
        // 10% случаев - от 0 до 1000
        randomTime = Math.floor(Math.random() * 1001);
      } else if (randomFactor < 0.2) {
        // Следующие 10% - от 1100 до 2000
        randomTime = Math.floor(Math.random() * 901) + 1100;
      } else if (randomFactor < 0.3) {
        // Следующие 10% - от 2100 до 3000
        randomTime = Math.floor(Math.random() * 901) + 2100;
      } else if (randomFactor < 0.4) {
        // Следующие 10% - от 3100 до 4000
        randomTime = Math.floor(Math.random() * 901) + 30100;
      } else if (randomFactor < 0.5) {
        // Следующие 10% - от 4100 до 5000
        randomTime = Math.floor(Math.random() * 901) + 4100;
      } else if (randomFactor < 0.6) {
        // Следующие 10% - от 5100 до 6000
        randomTime = Math.floor(Math.random() * 901) + 55100;
      } else if (randomFactor < 0.7) {
        // Следующие 10% - от 6100 до 7000
        randomTime = Math.floor(Math.random() * 901) + 66100;
      } else if (randomFactor < 0.8) {
        // Следующие 10% - от 7100 до 8000
        randomTime = Math.floor(Math.random() * 901) + 7100;
      } else if (randomFactor < 0.9) {
        // Следующие 10% - от 8100 до 9000
        randomTime = Math.floor(Math.random() * 901) + 28100;
      } else {
        // Последние 10% - от 9100 до 10000
        randomTime = Math.floor(Math.random() * 901) + 99100;
      }

      timeoutRef.current = setTimeout(() => {
        clearInterval(intervalRef.current);
        setGameOver(true);
        setResult(`-${betAmount.toFixed(2)} coin`);
        setIsPlaying(false);
        setBet(''); // Сбрасываем ставку
        setTimeout(() => {
          setGameOver(false);
          setResult(null);
        }, 1000); // 5 секунд задержки перед сбросом результата
      }, randomTime);
    }
  };

  const handleButtonRelease = () => {
    if (isPlaying) {
      clearInterval(intervalRef.current);
      clearTimeout(timeoutRef.current);
      setGameOver(true);
      const winnings = parseFloat((bet * value).toFixed(2));
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance + winnings).toFixed(2));
        localStorage.setItem('balance', newBalance.toString());
        return newBalance;
      });
      setResult(`+${winnings.toFixed(2)} coin`);
      setIsPlaying(false);
      setShowConfetti(true);
      setTimeout(() => {
        setGameOver(false);
        setResult(null);
      }, 1000); // 5 секунд задержки перед сбросом результата
      triggerHapticFeedbackSoft();
      setBet('');
    }
  };

  return (
    <div className="page3-container">
      {loading ? (
        <LoadingScreen />
      ) : (
        <div className="content">
          <div className="game-box">
            <div className="game-value">
              <div
                className={`value-text ${animateValue ? 'animate' : ''}`}
                onAnimationEnd={() => setAnimateValue(false)}
              >
                {value.toFixed(2)}x
              </div>
            </div>
            <div className="result-box">
              {gameOver && result && (
                <div className="result-message">
                  {result}
                </div>
              )}
            </div>
            {showConfetti && <ConfettiCanvas />}
            <div className="balance-box">
              <div className="balance-text">
                {balance.toFixed(2)} coins
              </div>
            </div>
            <div className="bet-box">
              <input
                type="text"
                value={bet}
                onChange={(e) => setBet(e.target.value)}
                placeholder="0.00"
                className={`bet-input ${animateInput ? 'animate' : ''}`}
                onAnimationEnd={() => setAnimateInput(false)}
              />
              <div className="bet-buttons">
                <button className="bet-button" onClick={() => handle10(0.1)}>10%</button>
                <button className="bet-button" onClick={() => handle10(0.5)}>50%</button>
                <button className="bet-button" onClick={() => handle10(1)}>ALL IN</button>
              </div>
            </div>
            <div className="tickets-box">
              <div className="tickets-text">
                🎫: {tickets}
              </div>
            </div>
            <div className="play-box">
              <button
                className={`play-button ${isPlaying ? 'playing' : ''}`}
                onMouseDown={handleButtonPress}
                onMouseUp={handleButtonRelease}
                onTouchStart={handleButtonPress}
                onTouchEnd={handleButtonRelease}
                disabled={isPlaying || !bet || isNaN(parseFloat(bet)) || parseFloat(bet) <= 0 || parseFloat(bet) > balance || tickets <= 0}
              >
                {isPlaying ? 'Playing...' : 'Play'}
              </button>
            </div>
            {tickets === 0 && (
              <div className="time-left-box">
                <div className="time-left-text">
                  Time left: {timeLeft}
                </div>
              </div>
            )}
            <div className="message-box">
              <div className="message-text">
                {message}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Page3;
