import React, { useState, useEffect, useRef } from 'react';
import './Page3.css';
import ConfettiCanvas from './ConfettiCanvas';
import LoadingScreen from './LoadingScreen';
import { useNavigate } from 'react-router-dom';

function Page3() {
  const [value, setValue] = useState(0.00);
  const [balance, setBalance] = useState(0.00);
  const [bet, setBet] = useState('');
  const [tickets, setTickets] = useState(9);
  const [message, setMessage] = useState('Введите ставку');
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [result, setResult] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [animateInput, setAnimateInput] = useState(false);
  const [animateValue, setAnimateValue] = useState(false);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState('');

  const intervalRef = useRef(null);
  const timeoutRef = useRef(null);
  const navigate = useNavigate(); // Хук для навигации

  const triggerHapticFeedback = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.selectionChanged();
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  const triggerHapticFeedbackError = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.notificationOccurred("error");
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  const triggerHapticFeedbackSuccess = () => {
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.HapticFeedback.notificationOccurred("success");
    } else if (navigator.vibrate) {
      navigator.vibrate(50); // Для Android устройств
    }
  };

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        navigate('/page1'); // Переход на Page1 при нажатии на кнопку назад
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  // Загрузка баланса и билетов из облачного хранилища
  useEffect(() => {
    const { CloudStorage } = window.Telegram.WebApp;

    CloudStorage.getItem('balance', (err, value) => {
      if (!err && value !== null) {
        setBalance(parseFloat(value));
      }
    });

    CloudStorage.getItem('tickets', (err, value) => {
      if (!err && value !== null) {
        setTickets(parseInt(value, 10));
      }
    });

    CloudStorage.getItem('lastReset', (err, value) => {
      if (!err && value !== null) {
        checkTicketsReset(new Date(value));
      } else {
        checkTicketsReset(null);
      }
    });
  }, []);

  const checkTicketsReset = (lastResetDate) => {
    const now = new Date();

    if (!lastResetDate || now.getDate() !== lastResetDate.getDate()) {
      setTickets(9);
      window.Telegram.WebApp.CloudStorage.setItem('tickets', '9');
      window.Telegram.WebApp.CloudStorage.setItem('lastReset', now.toString());
    }

    const interval = setInterval(() => {
      const now = new Date();
      const nextReset = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1);
      const difference = nextReset - now;

      let hours = Math.floor(difference / (1000 * 60 * 60));
      let minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      let seconds = Math.floor((difference % (1000 * 60)) / 1000);

      setTimeLeft(`${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);
    }, 1000);

    return () => clearInterval(interval);
  };

  useEffect(() => {
    const loadingTimeout = setTimeout(() => setLoading(false), 4000); // Hide loading screen after 4 seconds
    return () => clearTimeout(loadingTimeout);
  }, []);

  useEffect(() => {
    if (tickets === 0) {
      const calculateTimeLeft = () => {
        const now = new Date();
        const nextReset = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1);
        const difference = nextReset - now;

        let hours = Math.floor(difference / (1000 * 60 * 60));
        let minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
        let seconds = Math.floor((difference % (1000 * 60)) / 1000);

        setTimeLeft(`${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);
      };

      calculateTimeLeft();
      const timeInterval = setInterval(calculateTimeLeft, 1000);

      return () => clearInterval(timeInterval);
    }
  }, [tickets]);

  const handle10 = (cef) => {
    const betValue = Math.floor(balance * cef);
    setBet(betValue);
  };

  const handleButtonPress = () => {
    const betAmount = parseFloat(bet);
    if (!isNaN(betAmount) && betAmount <= balance && betAmount > 0 && tickets > 0 && !isPlaying) {
      setIsPlaying(true);
      setValue(1.00);
      setGameOver(false);
      setResult(null);
      setShowConfetti(false);
      setTickets((prevTickets) => {
        const newTickets = prevTickets - 1;
        window.Telegram.WebApp.CloudStorage.setItem('tickets', newTickets.toString());
        return newTickets;
      });
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance - betAmount).toFixed(2));
        window.Telegram.WebApp.CloudStorage.setItem('balance', newBalance.toString());
        return isNaN(newBalance) || newBalance < 0 ? 0 : newBalance;
      });

      const maxMultiplier = 20.00;
      intervalRef.current = setInterval(() => {
        setValue((prevValue) => parseFloat((prevValue + 0.01).toFixed(2)));
        triggerHapticFeedback();
      }, 100); // Увеличиваем коэффициент каждые 100 мс

      const randomFactor = Math.random();
      let randomTime;

      if (randomFactor < 0.1) {
        // 10% случаев - от 0 до 1000
        randomTime = Math.floor(Math.random() * 1001);
      } else if (randomFactor < 0.2) {
        // Следующие 10% - от 1100 до 2000
        randomTime = Math.floor(Math.random() * 901) + 1100;
      } else if (randomFactor < 0.3) {
        // Следующие 10% - от 2100 до 3000
        randomTime = Math.floor(Math.random() * 901) + 2100;
      } else if (randomFactor < 0.4) {
        // Следующие 10% - от 3100 до 4000
        randomTime = Math.floor(Math.random() * 901) + 3100;
      } else if (randomFactor < 0.5) {
        // Следующие 10% - от 4100 до 5000
        randomTime = Math.floor(Math.random() * 901) + 4100;
      } else if (randomFactor < 0.6) {
        // Следующие 10% - от 5100 до 6000
        randomTime = Math.floor(Math.random() * 901) + 5100;
      } else if (randomFactor < 0.7) {
        // Следующие 10% - от 6100 до 7000
        randomTime = Math.floor(Math.random() * 901) + 6100;
      } else if (randomFactor < 0.8) {
        // Следующие 10% - от 7100 до 8000
        randomTime = Math.floor(Math.random() * 901) + 7100;
      } else if (randomFactor < 0.9) {
        // Следующие 10% - от 8100 до 9000
        randomTime = Math.floor(Math.random() * 901) + 8100;
      } else {
        // Последние 10% - от 9100 до 10000
        randomTime = Math.floor(Math.random() * 901) + 9100;
      }

      timeoutRef.current = setTimeout(() => {
        clearInterval(intervalRef.current);
        setGameOver(true);
        setResult('lose');
        setIsPlaying(false);
        triggerHapticFeedbackError();
      }, randomTime);
    }
  };

  const handleButtonRelease = () => {
    if (!gameOver && isPlaying) {
      clearInterval(intervalRef.current);
      clearTimeout(timeoutRef.current);
      setIsPlaying(false);
      setGameOver(true);
      setResult('win');
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance + bet * value).toFixed(2));
        window.Telegram.WebApp.CloudStorage.setItem('balance', newBalance.toString());
        return newBalance;
      });
      setShowConfetti(true);
      triggerHapticFeedbackSuccess();
    }
  };

  return (
    <div className="page3">
      {loading ? (
        <LoadingScreen />
      ) : (
        <div>
          <div className="balance-info">
            <span className="balance-label">Баланс:</span>
            <span className="balance-value">{balance.toFixed(2)} ₽</span>
          </div>
          <div className="balance-info">
            <span className="balance-label">Ставка:</span>
            <span className="balance-value">{bet}</span>
          </div>
          <div className="balance-info">
            <span className="balance-label">Осталось билетов:</span>
            <span className="balance-value">{tickets}</span>
          </div>
          <div className="balance-info">
            <span className="balance-label">До следующего сброса:</span>
            <span className="balance-value">{timeLeft}</span>
          </div>
          <div className={`balance-input ${animateInput ? 'animate' : ''}`}>
            <input
              type="text"
              className="input-field"
              placeholder="Введите ставку"
              value={bet}
              onChange={(e) => setBet(e.target.value)}
              onFocus={() => setAnimateInput(true)}
              onBlur={() => setAnimateInput(false)}
            />
          </div>
          <div className={`value-display ${animateValue ? 'animate' : ''}`}>
            <span>{value.toFixed(2)}x</span>
          </div>
          <div className="button-container">
            <button
              className="bet-button"
              onMouseDown={handleButtonPress}
              onMouseUp={handleButtonRelease}
              onTouchStart={handleButtonPress}
              onTouchEnd={handleButtonRelease}
              disabled={isPlaying}
            >
              Играть
            </button>
          </div>
          {result === 'win' && (
            <div className="win-message">
              <span>Вы выиграли!</span>
            </div>
          )}
          {result === 'lose' && (
            <div className="lose-message">
              <span>Игра окончена</span>
            </div>
          )}
          {showConfetti && <ConfettiCanvas />}
        </div>
      )}
    </div>
  );
}

export default Page3;
