import React, { useState, useEffect, useRef } from 'react';
import './Page3.css';
import ConfettiCanvas from './ConfettiCanvas';
import LoadingScreen from './LoadingScreen';

function Page3() {
  const [value, setValue] = useState(0.00);
  const [balance, setBalance] = useState(() => {
    const savedBalance = localStorage.getItem('balance');
    return savedBalance ? parseFloat(savedBalance) : 0.00;
  });
  const [bet, setBet] = useState('');
  const [tickets, setTickets] = useState(() => {
    const savedTickets = localStorage.getItem('tickets');
    return savedTickets ? parseInt(savedTickets, 10) : 9;
  });
  const [message, setMessage] = useState('–í–≤–µ–¥–∏—Ç–µ —Å—Ç–∞–≤–∫—É');
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [result, setResult] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [animateInput, setAnimateInput] = useState(false);
  const [animateValue, setAnimateValue] = useState(false);
  const [loading, setLoading] = useState(true);
  const intervalRef = useRef(null);
  const timeoutRef = useRef(null);

  useEffect(() => {
    const betAmount = parseFloat(bet);
    if (bet === '') {
      setMessage('Token rate');
    } else if (isNaN(betAmount) || betAmount <= 0 || betAmount > balance) {
      setMessage('Not enough tokens');
    } else {
      setMessage('Charged');
    }
  }, [bet, balance]);

  useEffect(() => {
    setAnimateInput(true);
    setAnimateValue(true);
  }, []);

  useEffect(() => {
    const checkTicketsReset = () => {
      const now = new Date();
      const lastReset = localStorage.getItem('lastReset');
      const lastResetDate = lastReset ? new Date(lastReset) : null;

      if (!lastResetDate || now.getDate() !== lastResetDate.getDate()) {
        setTickets(9);
        localStorage.setItem('tickets', '9');
        localStorage.setItem('lastReset', now.toString());
      }
    };

    checkTicketsReset();

    const interval = setInterval(checkTicketsReset, 60000); // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–∞–∂–¥—É—é –º–∏–Ω—É—Ç—É

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const loadingTimeout = setTimeout(() => setLoading(false), 4000); // Hide loading screen after 4 seconds
    return () => clearTimeout(loadingTimeout);
  }, []);

  const handleBetChange = (e) => {
    const betValue = e.target.value;
    setBet(betValue);
  };
  const handle10 = (cef) => {
    const betValue = balance * cef
    setBet(betValue)
  }

  const handleButtonPress = () => {
    const betAmount = parseFloat(bet);
    if (!isNaN(betAmount) && betAmount <= balance && betAmount > 0 && tickets > 0 && !isPlaying) {
      setIsPlaying(true);
      setValue(1.00);
      setGameOver(false);
      setResult(null);
      setShowConfetti(false);
      setTickets((prevTickets) => {
        const newTickets = prevTickets - 1;
        localStorage.setItem('tickets', newTickets.toString());
        return newTickets;
      });
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance - betAmount).toFixed(2));
        return isNaN(newBalance) || newBalance < 0 ? 0 : newBalance;
      });

      const maxMultiplier = 20.00;
      intervalRef.current = setInterval(() => {
        setValue((prevValue) => parseFloat((prevValue + 0.01).toFixed(2)));
      }, 100); // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –∫–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç –∫–∞–∂–¥—ã–µ 100 –º—Å

      const randomFactor = Math.random();
      const randomTime = randomFactor < 0.5 ? 1000 : Math.floor(Math.random() * 19000) + 1000; // 50% –Ω–∞ –ø—Ä–æ–∏–≥—Ä—ã—à –ø—Ä–∏ x 1.00

      timeoutRef.current = setTimeout(() => {
        clearInterval(intervalRef.current);
        setGameOver(true);
        setResult(`-${betAmount.toFixed(2)} coin`);
        setIsPlaying(false);
        setBet(''); // –°–±—Ä–∞—Å—ã–≤–∞–µ–º —Å—Ç–∞–≤–∫—É
        setTimeout(() => {
          setGameOver(false);
          setResult(null);
        }, 1000); // 1 —Å–µ–∫—É–Ω–¥–∞ –∑–∞–¥–µ—Ä–∂–∫–∏
      }, randomTime);
    }
  };

  const handleButtonRelease = () => {
    if (!gameOver && isPlaying) {
      clearInterval(intervalRef.current);
      clearTimeout(timeoutRef.current);
      const betAmount = parseFloat(bet);
      const winnings = parseFloat((betAmount * value).toFixed(2));
      const newBalance = parseFloat((balance + winnings).toFixed(2));
      setBalance(isNaN(newBalance) || newBalance < 0 ? 0 : newBalance);
      localStorage.setItem('balance', (isNaN(newBalance) ? 0 : newBalance).toString());
      setResult(`+${winnings.toFixed(2)} coin`);
      setBet('');
      setValue(0.00);
      setIsPlaying(false);
      setShowConfetti(true);

      setTimeout(() => {
        setGameOver(false);
        setResult(null);
      }, 1000);
    }
  };

  useEffect(() => {
    if (gameOver) {
      localStorage.setItem('balance', (isNaN(balance) ? 0 : balance).toString());
    }
  }, [gameOver, balance]);

  if (loading) {
    return <LoadingScreen onEnd={() => setLoading(false)} />;
  }

  return (
    <div className="page3">
      <div className="ticket-display">{tickets} üé´</div>
      <div className={`value-display ${animateValue ? 'slide-in-value' : ''}`}>
        {gameOver ? <span style={{ color: 'red' }}>You lose</span> : `x ${value.toFixed(2)}`}
      </div>
      <button
        className="fingerprint-button"
        onMouseDown={handleButtonPress}
        onMouseUp={handleButtonRelease}
        onTouchStart={handleButtonPress}
        onTouchEnd={handleButtonRelease}
        disabled={parseFloat(bet) > balance || isNaN(parseFloat(bet)) || parseFloat(bet) <= 0 || isPlaying || tickets <= 0}
      >
        <img
          src="https://pngimg.com/uploads/fingerprint/fingerprint_PNG96.png"
          alt="Fingerprint"
          className="fingerprint-image"
        />
      </button>
      <div className={`input-container ${animateInput ? 'slide-in' : ''} ${isPlaying ? 'slide-out' : ''}`}>
        <label htmlFor="bet-input" style={{ color: message === '–í—ã –Ω–µ –º–æ–∂–µ—Ç–µ –∏–≥—Ä–∞—Ç—å' ? 'red' : 'white' }}>
          {message}
        </label>
        <div>{bet}</div>
      </div>
            <div className="bet-buttons">
        <button onClick={handle10}>1/10</button>
        <button onClick={handle10}>1/5</button>
        <button onClick={handle10(0.5)}>1/2</button>
        <button onClick={handle10(1)}>1/1</button>

      </div>
      <nav className={`nav ${isPlaying ? 'slide-out' : 'slide-in'}`}>
        {/* –í—Å—Ç–∞–≤—å—Ç–µ —Å—é–¥–∞ –≤–∞—à HTML –¥–ª—è –º–µ–Ω—é */}
      </nav>
      {result && <div className={`result ${result.startsWith('+') ? 'win' : 'lose'}`}>{result}</div>}
      {showConfetti && <ConfettiCanvas trigger={showConfetti} />}
    </div>
  );
}

export default Page3;
