import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [previousValue, setPreviousValue] = useState(56000);
  const [currentValue, setCurrentValue] = useState(56000);
  const canvasRef = useRef(null);

  const generateValue = () => {
    const newValue = Math.floor(Math.random() * (56345 - 55600 + 1)) + 55600;
    setCurrentValue(newValue);
  };

  useEffect(() => {
    if (currentValue !== previousValue) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      const scale = window.devicePixelRatio || 1;
      canvas.width = 500 * scale;
      canvas.height = 300 * scale;
      ctx.scale(scale, scale);

      ctx.clearRect(0, 0, canvas.width, canvas.height);

      const difference = currentValue - previousValue;
      const step = difference > 0 ? 1 : -1;
      let progress = 0;

      const startY = 150 - ((previousValue - 56000) / 345) * 120;
      const targetY = 150 - ((currentValue - 56000) / 345) * 120;
      const x = (canvas.width / scale) / 2 - 15;

      const interval = setInterval(() => {
        progress += step;

        ctx.clearRect(x, 0, 30, canvas.height / scale);

        const height = Math.abs(progress);
        const rectY = difference > 0 ? startY - height : startY;

        ctx.fillStyle = difference > 0 ? 'green' : 'red';
        ctx.fillRect(x, rectY, 30, height);

        if (Math.abs(progress) >= Math.abs(targetY - startY)) {
          clearInterval(interval);
          setPreviousValue(currentValue);
        }
      }, 20);

      return () => clearInterval(interval);
    }
  }, [currentValue, previousValue]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={generateValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Generate Value
      </button>
    </div>
  );
};

export default Page4;
