// StoryComponent.js
import React, { useState } from 'react';
import './StoryComponent.css';

const stories = [
  {
    gif: 'https://i.ibb.co/tPg7LNh/photo-output.jpg',
    text1: 'First Story Text 1',
    text2: 'First Story Text 2',
  },
  {
    gif: 'https://example.com/yourgif2.gif',
    text1: 'Second Story Text 1',
    text2: 'Second Story Text 2',
  },
  {
    gif: 'https://example.com/yourgif3.gif',
    text1: 'Third Story Text 1',
    text2: 'Third Story Text 2',
    buttons: true,
  },
];

const StoryComponent = ({ onComplete }) => {
  const [currentStory, setCurrentStory] = useState(0);

  const handleNextStory = () => {
    if (currentStory < stories.length - 1) {
      setCurrentStory(currentStory + 1);
    } else {
      onComplete();
    }
  };

  const current = stories[currentStory];

  return (
    <div className="story-container" onClick={handleNextStory}>
      <img src={current.gif} alt="story gif" className="story-gif" />
      <div className="story-text1">{current.text1}</div>
      <div className="story-text2">{current.text2}</div>
      <div className="story-indicator">
        {stories.map((_, index) => (
          <span
            key={index}
            className={`indicator-dot ${index === currentStory ? 'active' : ''}`}
          />
        ))}
      </div>
      {current.buttons && (
        <div className="story-buttons">
          <button onClick={onComplete}>Button 1</button>
          <button onClick={onComplete}>Button 2</button>
        </div>
      )}
    </div>
  );
};

export default StoryComponent;
