import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  // Настройки для карусели
  const settings = {
    dots: true, // Показывать точки навигации
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Количество видимых слайдов
    slidesToScroll: 1, // Прокручивать по одному слайду
    swipeToSlide: true, // Свайп для перелистывания
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <div className="level-stars"></div>
        <h1 className="level-title">Level Page</h1>
        <div className="carousel-container">
          <Slider {...settings}>
            <div className="carousel-card">Card 1</div>
            <div className="carousel-card">Card 2</div>
            <div className="carousel-card">Card 3</div>
            <div className="carousel-card">Card 4</div>
            <div className="carousel-card">Card 5</div>
          </Slider>
        </div>
      </div>
    </div>
  );
};

export default LevelPage;
