import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(Number(e.target.value));
  };

  const handleClick = (newValue) => {
    setValue(newValue);
  };

  const marks = [0, 25, 50, 75, 100];

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', position: 'relative' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
            position: 'absolute',
            top: '50%',
            transform: 'translateY(-50%)',
          }}
        />
      </div>
      <div style={{ position: 'relative', width: '100%' }}>
        <input 
          type="range" 
          min="0" 
          max="100" 
          value={value} 
          onChange={handleChange} 
          style={{ width: '100%' }}
        />
        <div style={{ position: 'absolute', top: '50%', width: '100%', transform: 'translateY(-50%)', display: 'flex', justifyContent: 'space-between' }}>
          {marks.map(mark => (
            <div 
              key={mark}
              onClick={() => handleClick(mark)}
              style={{
                position: 'relative',
                width: '0',
                height: '0',
                zIndex: 1,
              }}
            >
              <div
                style={{
                  width: '12px',
                  height: '12px',
                  borderRadius: '50%',
                  backgroundColor: mark <= value ? '#007bff' : '#ccc',
                  transform: 'translateX(-50%)',
                  cursor: 'pointer',
                }}
              ></div>
            </div>
          ))}
        </div>
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px', marginTop: '10px' }}>
        {marks.map(mark => (
          <span key={mark}>{mark}%</span>
        ))}
      </div>
    </div>
  );
};

export default PercentageSlider;
