import React, { useEffect, useState } from 'react';
import { useSpring, animated, useSprings } from 'react-spring';
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [bubbles, setBubbles] = useState([0, 1, 2, 3, 4, 5, 6, 7]);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const handleBubbleClick = (index) => {
    setBubbles(bubbles.filter((bubble) => bubble !== index));
  };

  const springs = useSprings(
    bubbles.length,
    bubbles.map((bubble, i) => ({
      from: { transform: 'scale(1) translate(0px, 0px)' },
      to: {
        transform: `scale(1) translate(${(i - 3.5) * 40}px, 0px)`,
      },
      config: { tension: 220, friction: 20 },
    }))
  );

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <h1 className="level-title">Level Page</h1>
        <div className="bubble-container">
          {springs.map((springStyles, i) => (
            <animated.div
              key={bubbles[i]}
              className="bubble"
              style={springStyles}
              onClick={() => handleBubbleClick(bubbles[i])}
            />
          ))}
        </div>
      </div>
      <div className="level-content"></div>
    </div>
  );
};

export default LevelPage;
