import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const Page4 = () => {
  const [balance, setBalance] = useState(0);
  const [calibrationPrice, setCalibrationPrice] = useState(null);
  const [pricePoints, setPricePoints] = useState([]);
  const [currentPrice, setCurrentPrice] = useState(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    // Получение баланса из CloudStorage
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          setBalance(initialBalance);
        }
      });
    };
    getInitialData();
  }, []);

  useEffect(() => {
    const fetchBitcoinPrice = async () => {
      try {
        const response = await axios.get('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
        const newPrice = parseFloat(response.data.bpi.USD.rate.replace(',', ''));

        if (calibrationPrice === null) {
          setCalibrationPrice(newPrice);
          setCurrentPrice(newPrice);
        } else {
          const lastPrice = currentPrice || newPrice;
          const steps = 60; // Количество промежуточных шагов (один шаг каждые 50 мс на протяжении 3 секунд)
          const stepSize = (newPrice - lastPrice) / steps;

          for (let i = 1; i <= steps; i++) {
            setTimeout(() => {
              const interpolatedPrice = lastPrice + stepSize * i;
              setPricePoints((prevPoints) => {
                if (prevPoints.length >= 30) prevPoints.shift(); // Удаление старых точек
                return [...prevPoints, { price: interpolatedPrice, time: Date.now() }];
              });
              setCurrentPrice(interpolatedPrice);
            }, i * 50);
          }
        }
      } catch (error) {
        console.error('Error fetching Bitcoin price:', error);
      }
    };

    fetchBitcoinPrice();
    const interval = setInterval(fetchBitcoinPrice, 3000);

    return () => clearInterval(interval);
  }, [calibrationPrice, currentPrice]);

  useEffect(() => {
    const drawGraph = () => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      const width = canvas.width;
      const height = canvas.height;

      const maxDeviation = 400;
      ctx.clearRect(0, 0, width, height);

      if (pricePoints.length > 1) {
        const centerY = height / 2;

        ctx.beginPath();
        const initialX = width - 20; // Начинаем с правого края
        ctx.moveTo(initialX, centerY);

        pricePoints.forEach((point, index) => {
          const x = initialX - ((pricePoints.length - 1 - index) * (width / 30));
          const y = centerY - ((point.price - calibrationPrice) * (maxDeviation / maxDeviation));

          ctx.lineTo(x, y);

          // Отображение цены рядом с точкой
          if (index === pricePoints.length - 1) {
            ctx.font = '14px Arial';
            ctx.fillStyle = '#000';
            ctx.fillText(point.price.toFixed(2), x + 5, y - 10);
            ctx.moveTo(x, y); // Поддержка соединения линий
          }
        });

        ctx.strokeStyle = '#007bff';
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    };

    drawGraph();
  }, [pricePoints, calibrationPrice]);

  return (
    <div style={{ position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#f5f5f5' }}>
      <div style={{ position: 'absolute', top: '20px', right: '20px', display: 'flex', alignItems: 'center', fontSize: '24px', fontWeight: 'bold' }}>
        <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance Icon" style={{ marginRight: '10px', width: '24px', height: '24px' }} />
        {balance.toLocaleString()}
      </div>
      <canvas ref={canvasRef} width={800} height={280} style={{ marginTop: '30px', border: '1px solid #ccc' }}></canvas>
    </div>
  );
};

export default Page4;
