import React, { useEffect, useState } from 'react';
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [bubbles, setBubbles] = useState([1, 2, 3, 4, 5, 6, 7, 8]);

  // Запускаем анимацию через небольшой таймаут после загрузки страницы
  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  // Удаление пузырька
  const handleBubbleClick = (index) => {
    setBubbles((prevBubbles) => prevBubbles.filter((_, i) => i !== index));
  };

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <h1>Level Page</h1>
        <div className="bubble-container">
          {bubbles.map((bubble, index) => (
            <div
              key={bubble}
              className="bubble"
              onClick={() => handleBubbleClick(index)}
            >
              {bubble}
            </div>
          ))}
        </div>
      </div>
      <div className="level-content">
      </div>
    </div>
  );
};

export default LevelPage;
