import React, { useEffect, useState } from 'react';
import { Chart } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, TimeScale, Tooltip, Title } from 'chart.js';
import { CandlestickController, CandlestickElement } from 'chartjs-chart-financial';
import 'chartjs-adapter-date-fns';

// Регистрация необходимых компонентов
ChartJS.register(CategoryScale, LinearScale, TimeScale, Tooltip, Title, CandlestickController, CandlestickElement);

const Page4 = () => {
  const [data, setData] = useState({
    labels: [],
    datasets: [
      {
        label: 'Candlestick Data',
        data: [],
        borderColor: 'rgba(75, 192, 192, 1)',
      },
    ],
  });

  const generateRandomCandle = (prevClose) => {
    const open = prevClose;
    const close = open + (Math.random() - 0.5) * 10;
    const high = Math.max(open, close) + Math.random() * 5;
    const low = Math.min(open, close) - Math.random() * 5;

    return {
      x: new Date(),
      o: open,
      h: high,
      l: low,
      c: close,
    };
  };

  const addCandle = () => {
    const newData = [...data.datasets[0].data];
    const newLabels = [...data.labels];

    if (newData.length >= 20) {
      newData.shift();
      newLabels.shift();
    }

    const lastCandle = newData.length ? newData[newData.length - 1] : { c: 50 };
    const newCandle = generateRandomCandle(lastCandle.c);

    newData.push(newCandle);
    newLabels.push(newCandle.x);

    setData({
      labels: newLabels,
      datasets: [{
        ...data.datasets[0],
        data: newData,
      }],
    });
  };

  useEffect(() => {
    const interval = setInterval(addCandle, 5000);  // Каждая свеча создается каждые 5 секунд
    return () => clearInterval(interval);
  }, [data]);

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#f5f5f5',
  };

  const chartStyle = {
    width: '80%',
    height: '80%',
  };

  return (
    <div style={containerStyle}>
      <div style={chartStyle}>
        <Chart
          type="candlestick"
          data={data}
          options={{
            maintainAspectRatio: false,
            scales: {
              x: {
                type: 'time',
                time: {
                  unit: 'second',
                  stepSize: 5,  // Шаг оси X - 5 секунд
                },
              },
              y: {
                beginAtZero: false,
              },
            },
          }}
        />
      </div>
    </div>
  );
};

export default Page4;
