import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate, useLocation } from 'react-router-dom';
import Home from './Home';
import Page1 from './Page1';
import Page2 from './Page2';
import Page3 from './Page3';
import './App.css';
import './Navigation.css';

function App() {
  const [pageLoaded, setPageLoaded] = useState(false);

  useEffect(() => {
    const platform = navigator.platform.toLowerCase();
    if (platform.includes("win") || platform.includes("mac")) {
      console.log("Open on your mobile device");
      setPageLoaded(false);
    } else {
      setPageLoaded(true);
    }

    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.expand();
      window.Telegram.WebApp.themeParams.bg_color = "#000000";
      window.Telegram.WebApp.themeParams.secondary_bg_color = "#000000";
      window.Telegram.WebApp.themeParams.text_color = "#FFFFFF";
      window.Telegram.WebApp.themeParams.hint_color = "#999999";
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/page1" element={<Page1 />} />
        <Route path="/page2" element={<Page2 />} />
        <Route path="/page3" element={<Page3 />} />
      </Routes>
      {pageLoaded && <Navigation />}
    </Router>
  );
}

function Navigation() {
  const navigate = useNavigate();
  const location = useLocation();
  const isHomePage = location.pathname === '/';

  useEffect(() => {
    if (window.Telegram.WebApp) {
      if (isHomePage) {
        window.Telegram.WebApp.MainButton.hide();
      } else {
        window.Telegram.WebApp.MainButton.setText('Назад');
        window.Telegram.WebApp.MainButton.show();
        window.Telegram.WebApp.MainButton.onClick(() => navigate(-1));
      }
    }
  }, [isHomePage, navigate]);

  return (
    <nav className="nav">
      <Link to="/" className="nav-link">
        <img src="https://upload.wikimedia.org/wikipedia/commons/1/12/Sodium-crystal-3D-vdW.png" alt="Home" className="nav-icon" />
        <span>Mining</span>
      </Link>
      <Link to="/page1" className="nav-link">
        <img src="https://cdn3d.iconscout.com/3d/premium/thumb/game-controller-4035922-3342601.png" alt="Page 1" className="nav-icon" />
        <span>Game</span>
      </Link>
      <Link to="/page2" className="nav-link">
        <img src="https://cdn3d.iconscout.com/3d/premium/thumb/magnetic-power-7074108-5752015.png?f=webp" alt="Page 2" className="nav-icon" />
        <span>Friends</span>
      </Link>
      <Link to="/page3" className="nav-link">
        <img src="https://cdn3d.iconscout.com/3d/premium/thumb/magnetic-power-7074108-5752015.png?f=webp" alt="Page 3" className="nav-icon" />
        <span>Page 3</span>
      </Link>
    </nav>
  );
}

export default App;
