import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [previousValue, setPreviousValue] = useState(null);
  const [currentValue, setCurrentValue] = useState(null);
  const [rectangles, setRectangles] = useState([]); // массив для хранения прямоугольников
  const [centerValue, setCenterValue] = useState(null); // динамический центр по оси Y
  const canvasRef = useRef(null);

  const fetchBitcoinPrice = async () => {
    try {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
      const data = await response.json();
      const bitcoinPrice = Math.floor(data.bpi.USD.rate_float);
      return bitcoinPrice;
    } catch (error) {
      console.error('Ошибка при получении цены биткоина:', error);
      return 60600; // fallback значение на случай ошибки
    }
  };

  const generateNewValue = async (centerValue) => {
    const randomDiff = Math.floor(Math.random() * 691) - 345; // случайное число от -345 до 345
    const newValue = centerValue + randomDiff;
    setCurrentValue(newValue);
  };

  const adjustCenter = (newValue) => {
    const upperLimit = centerValue + 500;
    const lowerLimit = centerValue - 500;

    if (newValue > upperLimit) {
      setCenterValue(newValue - 500);
    } else if (newValue < lowerLimit) {
      setCenterValue(newValue + 500);
    }
  };

  useEffect(() => {
    const drawRectangle = () => {
      if (currentValue !== previousValue) {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        const scale = window.devicePixelRatio || 1;
        canvas.width = 500 * scale;
        canvas.height = 6000 * scale;
        ctx.scale(scale, scale);

        const difference = currentValue - previousValue;
        const step = difference > 0 ? 1 : -1;
        let progress = 0;

        const startY = 3000 - ((previousValue - centerValue) / 345) * 120;
        const targetY = 3000 - ((currentValue - centerValue) / 345) * 120;
        const rectWidth = 30;
        const interval = setInterval(() => {
          progress += step;

          ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

          // смещение всех прямоугольников влево
          const updatedRectangles = rectangles.map((rect) => ({
            ...rect,
            x: rect.x - rectWidth,
          }));

          // рисование всех прямоугольников
          updatedRectangles.forEach((rect) => {
            ctx.fillStyle = rect.color;
            ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
          });

          // рисование текущего прямоугольника
          const height = Math.abs(progress);
          const rectY = difference > 0 ? startY - height : startY;
          ctx.fillStyle = difference > 0 ? 'green' : 'red';
          ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

          if (Math.abs(progress) >= Math.abs(targetY - startY)) {
            clearInterval(interval);

            // добавляем новый прямоугольник в массив
            updatedRectangles.push({
              x: canvas.width / scale / 2 - 15,
              y: rectY,
              width: rectWidth,
              height: height,
              color: difference > 0 ? 'green' : 'red',
            });

            setRectangles(updatedRectangles);
            setPreviousValue(currentValue);
            adjustCenter(currentValue); // Корректируем центр оси Y
            generateNewValue(currentValue); // Запускаем процесс для нового значения
          }
        }, 20);

        return () => clearInterval(interval);
      }
    };

    if (currentValue !== null && previousValue !== null) {
      drawRectangle();
    }
  }, [currentValue, previousValue, rectangles, centerValue]);

  useEffect(() => {
    const initializeGraph = async () => {
      const bitcoinPrice = await fetchBitcoinPrice();
      setPreviousValue(bitcoinPrice);
      setCurrentValue(bitcoinPrice);
      setCenterValue(bitcoinPrice); // Устанавливаем начальный центр
      generateNewValue(bitcoinPrice); // Запускаем процесс для первого значения
    };

    initializeGraph();
  }, []);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      {currentValue}
    </div>
  );
};

export default Page4;
