import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [elementsVisible, setElementsVisible] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0); // Прогресс в процентах
  const [balance, setBalance] = useState(0); // Баланс пользователя
  const [balanceRate, setBalanceRate] = useState(1); // Текущий "рейт" баланса
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();

  const leftTexts = ['Left Text 1', 'Left Text 2', 'Left Text 3'];
  const rightTexts = ['Right Text 1', 'Right Text 2', 'Right Text 3'];

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        setIsExiting(true);
        setTimeout(() => {
          navigate('/');
        }, 850);
      });
    }
    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    // Получаем баланс и баланс рейт из CloudStorage
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate', 'progress', 'level'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialBalanceRate = result.balanceRate ? parseInt(result.balanceRate, 10) : 1;
          const initialProgress = result.progress ? parseInt(result.progress, 10) : 0;
          const initialLevel = result.level ? parseInt(result.level, 10) : 1;

          setBalance(initialBalance);
          setBalanceRate(initialBalanceRate);
          setProgress(initialProgress);
          setLevel(initialLevel);
        }
      });
    };

    getInitialData();
  }, []);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);

    setTimeout(() => {
      setElementsVisible(true);
    }, 550);
  }, []);

  const handleBlockClick = () => {
    // Логика для первого блока
    const balanceThresholds = [1, 5, 10, 20]; // Условия для прогресса по рейту
    const requiredBalance = balanceThresholds[balanceRate - 1] || 0;

    if (balance >= requiredBalance) {
      // Если прогресс больше или равен 100%, обновляем уровень
      if (progress >= 100) {
        alert('Level Up!');
        const newLevel = level + 1;
        setLevel(newLevel);
        setProgress(0);

        // Обновляем уровень и сбрасываем прогресс в CloudStorage
        window.Telegram.WebApp.CloudStorage.setItem('level', newLevel.toString(), (error) => {
          if (error) console.error('Failed to update level in cloud storage:', error);
        });
        window.Telegram.WebApp.CloudStorage.setItem('progress', '0', (error) => {
          if (error) console.error('Failed to reset progress in cloud storage:', error);
        });
      } else {
        const newProgress = progress + 20;
        setProgress(newProgress);

        // Обновляем прогресс в CloudStorage
        window.Telegram.WebApp.CloudStorage.setItem('progress', newProgress.toString(), (error) => {
          if (error) console.error('Failed to update progress in cloud storage:', error);
        });
      }

      // Обновляем баланс рейт
      const newBalanceRate = balanceRate + 1;
      setBalanceRate(newBalanceRate);
      window.Telegram.WebApp.CloudStorage.setItem('balanceRate', newBalanceRate.toString(), (error) => {
        if (error) console.error('Failed to update balanceRate in cloud storage:', error);
      });
    } else {
      alert(`Not enough balance! You need at least ${requiredBalance} to proceed.`);
    }
  };

  const getProgressColor = () => {
    const colors = ['#4caf50', '#2196f3', '#ff9800', '#9c27b0', '#e91e63'];
    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      {/* Верхняя часть */}
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          {level} LEVEL
        </h1>

        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          margin: '10px auto',
          backgroundColor: 'rgba(221, 221, 221, 0.1)',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Блоки */}
        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          cursor: (balance >= 1) ? 'pointer' : 'not-allowed',
          opacity: (balance >= 1) ? 1 : 0.5,
        }} onClick={handleBlockClick}>
          <span>{leftTexts[0]}</span>
          <span>{rightTexts[0]}</span>
        </div>

        {/* Блоки для будущих условий */}
        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: 0.5, // Сделаем их неактивными пока
        }}>
          <span>{leftTexts[1]}</span>
          <span>{rightTexts[1]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: 0.5, // Сделаем их неактивными пока
        }}>
          <span>{leftTexts[2]}</span>
          <span>{rightTexts[2]}</span>
        </div>
      </div>

      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
