import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
          }}
        />
      </div>
      <input 
        type="range" 
        min="0" 
        max="100" 
        value={value} 
        onChange={handleChange} 
        style={{ 
          width: '100%', 
          appearance: 'none', 
          height: '8px', 
          borderRadius: '4px', 
          background: '#ccc', 
          outline: 'none',
          cursor: 'pointer',
          accentColor: '#4CAF50', // Change this color to match the theme you want
        }} 
      />
    </div>
  );
};

export default PercentageSlider;
