import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const values = [
    78, 45, 92, 110, 67, 84, 56, 90, 102, 48, 88, 61, 70, 109, 81, 52, 94, 66, 75, 118,
    44, 95, 104, 63, 71, 85, 39, 111, 79, 59, 73, 113, 87, 40, 103, 68, 82, 119, 50, 97,
    // Остальные значения из вашего массива
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [dataPoints, setDataPoints] = useState([]);
  const [fixedValue, setFixedValue] = useState(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentIndex < values.length) {
        setDataPoints(prev => [...prev, values[currentIndex]]);
        setCurrentIndex(currentIndex + 1);
      }
    }, 1000); // Установлено 200 миллисекунд для более плавного графика

    return () => clearInterval(interval);
  }, [currentIndex]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      for (let i = 0; i < dataPoints.length - 1; i++) {
        const x1 = (canvas.width / 2) - (dataPoints.length - 1 - i) * 10;
        const y1 = canvas.height - (dataPoints[i] / 150) * canvas.height;
        const x2 = (canvas.width / 2) - (dataPoints.length - 2 - i) * 10;
        const y2 = canvas.height - (dataPoints[i + 1] / 150) * canvas.height;

        const color = fixedValue !== null ? (dataPoints[i + 1] > fixedValue ? 'green' : 'red') : '#FFD700';
        ctx.strokeStyle = color;
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.stroke();
        ctx.closePath();

        // Отображаем значение рядом с точкой
        ctx.fillStyle = color;
        ctx.font = '14px Arial';
        ctx.fillText(dataPoints[i], x1 + 5, y1 - 5);
      }

      if (fixedValue !== null) {
        const fixedY = canvas.height - (fixedValue / 150) * canvas.height;
        ctx.strokeStyle = 'lightblue';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(0, fixedY);
        ctx.lineTo(canvas.width, fixedY);
        ctx.stroke();
      }
    }
  }, [dataPoints, fixedValue]);

  const handleFixValue = () => {
    setFixedValue(values[currentIndex - 1]);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        width={500}
        height={300}
        style={{ border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button 
        onClick={handleFixValue} 
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
