import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [previousValue, setPreviousValue] = useState(56000);
  const [currentValue, setCurrentValue] = useState(56000);
  const [rectangles, setRectangles] = useState([]);
  const canvasRef = useRef(null);

  const generateValue = () => {
    const newValue = Math.floor(Math.random() * (56345 - 55600 + 1)) + 55600;
    setCurrentValue(newValue);
  };

  useEffect(() => {
    if (currentValue !== previousValue) {
      const difference = currentValue - previousValue;
      const isPositive = difference > 0;

      const newRectangle = {
        id: Date.now(),
        startValue: previousValue,
        endValue: currentValue,
        color: isPositive ? 'green' : 'red',
      };

      setRectangles(prev => [...prev, newRectangle]);
    }
  }, [currentValue, previousValue]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 300 * scale;
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    rectangles.forEach((rect, index) => {
      const x = 200 + index * 35;
      const startY = 150 - ((rect.startValue - 56000) / 345) * 120;
      const endY = 150 - ((rect.endValue - 56000) / 345) * 120;
      const height = endY - startY;

      ctx.fillStyle = rect.color;
      ctx.fillRect(x, startY, 30, height);
    });
  }, [rectangles]);

  useEffect(() => {
    if (currentValue !== previousValue) {
      const difference = currentValue - previousValue;
      const step = difference / Math.abs(difference); // 1 или -1
      let progress = 0;

      const interval = setInterval(() => {
        progress += step;

        setPreviousValue(prev => prev + step);

        if (Math.abs(progress) >= Math.abs(difference)) {
          clearInterval(interval);
          setPreviousValue(currentValue);
        }
      }, 20);

      return () => clearInterval(interval);
    }
  }, [currentValue, previousValue]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={generateValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Generate Value
      </button>
    </div>
  );
};

export default Page4;
