import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const [previousValue, setPreviousValue] = useState(56000);
    const [currentValue, setCurrentValue] = useState(56000);
    const [rectangles, setRectangles] = useState([]);
    const canvasRef = useRef(null);

    const generateValue = () => {
        const minValue = currentValue - 2500;
        const maxValue = currentValue + 2500;
        const newValue = Math.floor(Math.random() * (maxValue - minValue + 1)) + minValue;
        setCurrentValue(newValue);
    };

    useEffect(() => {
        if (currentValue !== previousValue) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');

            const scale = window.devicePixelRatio || 1;
            canvas.width = 500 * scale;
            canvas.height = 300 * scale;
            ctx.scale(scale, scale);

            const difference = currentValue - previousValue;
            const step = difference > 0 ? 1 : -1;
            let progress = 0;

            const startY = 150 - ((previousValue - currentValue) / 5000) * 120;
            const targetY = 150 - ((currentValue - currentValue) / 5000) * 120;
            const rectWidth = 14; // ширина прямоугольника уменьшена в 2 раза
            const interval = setInterval(() => {
                progress += step;

                ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

                const updatedRectangles = rectangles.map((rect) => ({
                    ...rect,
                    x: rect.x - rectWidth - 1, // добавлен промежуток в 1 пиксель
                }));

                updatedRectangles.forEach((rect) => {
                    ctx.fillStyle = rect.color;
                    ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
                });

                const height = Math.abs(progress);
                const rectY = difference > 0 ? startY - height : startY;
                ctx.fillStyle = difference > 0 ? 'green' : 'red';
                ctx.fillRect(canvas.width / scale / 2 - rectWidth / 2, rectY, rectWidth, height);

                if (Math.abs(progress) >= Math.abs(targetY - startY)) {
                    clearInterval(interval);

                    updatedRectangles.push({
                        x: canvas.width / scale / 2 - rectWidth / 2,
                        y: rectY,
                        width: rectWidth,
                        height: height,
                        color: difference > 0 ? 'green' : 'red',
                    });

                    setRectangles(updatedRectangles);
                    setPreviousValue(currentValue);
                }
            }, 20);

            return () => clearInterval(interval);
        }
    }, [currentValue, previousValue, rectangles]);

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <canvas
                ref={canvasRef}
                style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
            ></canvas>
            <button
                onClick={generateValue}
                style={{
                    padding: '10px 20px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    backgroundColor: '#4CAF50',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px'
                }}>
                Generate Value
            </button>
        </div>
    );
};

export default Page4;
