import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './Home';
import Page1 from './Page1';
import Page2 from './Page2';
import Page3 from './Page3'; // Импортируем Page3
import { useThemeParams, useInitData, useMainButton } from '@twa-dev/sdk';
import './App.css';
import './Navigation.css';

function App() {
  const [pageLoaded, setPageLoaded] = useState('');
  const themeParams = useThemeParams();
  const initData = useInitData();
  const mainButton = useMainButton();

  useEffect(() => {
    const platform = navigator.platform.toLowerCase();
    if (platform.includes("win") || platform.includes("mac")) {
      console.log("Open on your mobile device");
      setPageLoaded(false);
    } else {
      setPageLoaded(true);
    }

    // Инициализация и настройка Telegram Web App SDK
    if (initData) {
      themeParams.setBackgroundColor('#0000FF'); // Замените на нужный вам цвет
      themeParams.setButtonColor('#FFFFFF'); // Цвет текста кнопки "Отменить"
      mainButton.setText('Cancel'); // Измените текст кнопки "Отменить" на английский "Cancel"
      mainButton.show();
    }
  }, [initData, themeParams, mainButton]);

  if (!pageLoaded) {
    return (
      <div style={{ backgroundColor: 'black', color: 'white', height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <h1>Denied</h1>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/page1" element={<Page1 />} />
          <Route path="/page2" element={<Page2 />} />
          <Route path="/page3" element={<Page3 />} /> {/* Добавляем маршрут для Page3 */}
        </Routes>
        <nav className="nav">
          <Link to="/" className="nav-link">
            <img src="https://upload.wikimedia.org/wikipedia/commons/1/12/Sodium-crystal-3D-vdW.png" alt="Home" className="nav-icon" />
            <span>Mining</span>
          </Link>
          <Link to="/page1" className="nav-link">
            <img src="https://cdn3d.iconscout.com/3d/premium/thumb/game-controller-4035922-3342601.png" alt="Page 1" className="nav-icon" />
            <span>Game</span>
          </Link>
          <Link to="/page2" className="nav-link">
            <img src="https://cdn3d.iconscout.com/3d/premium/thumb/magnetic-power-7074108-5752015.png?f=webp" alt="Page 2" className="nav-icon" />
            <span>Friends</span>
          </Link>
        </nav>
      </div>  
    </Router>
  );
}

export default App;
