import React, { useState } from 'react';
import { useSprings, animated } from 'react-spring';

// Компонент отдельного пузырька
const Bubble = ({ size, style, onClick }) => {
  const bubbleStyle = {
    width: size,
    height: size,
    borderRadius: '50%',
    backgroundColor: 'skyblue',
    position: 'absolute',
    ...style,
  };

  return <animated.div style={bubbleStyle} onClick={onClick} />;
};

// Основной компонент с пузырьками
const Bubbles = () => {
  // Начальные данные пузырьков с случайным расположением
  const [bubbles, setBubbles] = useState([
    { id: 1, size: 50, x: -60, y: -60 },
    { id: 2, size: 70, x: 80, y: -30 },
    { id: 3, size: 60, x: -100, y: 80 },
    { id: 4, size: 40, x: 0, y: 100 },
    { id: 5, size: 55, x: 100, y: 50 },
  ]);

  // Анимация для всех пузырьков
  const [springs, api] = useSprings(
    bubbles.length, 
    (index) => ({
      to: { x: bubbles[index].x, y: bubbles[index].y },
      config: { tension: 180, friction: 12 },
    })
  );

  // Удаление пузырька и перерасчет позиций оставшихся
  const removeBubble = (id) => {
    const updatedBubbles = bubbles.filter((bubble) => bubble.id !== id);

    // Обновляем позиции оставшихся пузырьков
    const newPositions = updatedBubbles.map((bubble, index) => ({
      ...bubble,
      x: Math.random() * 200 - 100, // Генерируем случайные позиции с учетом смещения
      y: Math.random() * 200 - 100,
    }));

    setBubbles(newPositions);

    // Применяем новую анимацию для всех пузырьков
    api.start((index) => ({
      x: newPositions[index]?.x,
      y: newPositions[index]?.y,
    }));
  };

  return (
    <div
      style={{
        position: 'relative',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {springs.map((style, index) => (
        <Bubble
          key={bubbles[index].id}
          size={bubbles[index].size}
          style={{
            transform: style.x.to((x) => `translate3d(${x}px, ${style.y.get()}px, 0)`),
          }}
          onClick={() => removeBubble(bubbles[index].id)}
        />
      ))}
    </div>
  );
};

export default Bubbles;
