import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  const trackStyle = {
    width: '100%',
    appearance: 'none',
    height: '8px',
    borderRadius: '4px',
    background: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="8"><path d="M0 4 Q 25 0, 50 4 T 100 4 T 150 0 T 200 4 T 250 0 T 300 4 T 350 0 T 400 4" stroke="%234CAF50" fill="transparent"/></svg>')`,
    outline: 'none',
    cursor: 'pointer',
  };

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
          }}
        />
      </div>
      <input 
        type="range" 
        min="0" 
        max="100" 
        value={value} 
        onChange={handleChange} 
        style={trackStyle}
      />
    </div>
  );
};

export default PercentageSlider;
