import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const values = Array.from({ length: 100 }, () => Math.floor(Math.random() * 90) + 30); // Массив из 100 значений от 30 до 119
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dataPoints, setDataPoints] = useState([]);
  const [fixedValue, setFixedValue] = useState(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      if (currentIndex < values.length) {
        setDataPoints(prev => [...prev, values[currentIndex]]);
        setCurrentIndex(currentIndex + 1);
      }
    }, 100);

    return () => clearInterval(interval);
  }, [currentIndex]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Рисуем шкалу
    ctx.fillStyle = 'black';
    ctx.font = '12px Arial';
    for (let i = 0; i <= 150; i += 30) {
      const y = canvas.height - (i / 150) * canvas.height;
      ctx.fillText(i, 5, y);
      ctx.beginPath();
      ctx.moveTo(30, y);
      ctx.lineTo(canvas.width, y);
      ctx.strokeStyle = '#e0e0e0';
      ctx.stroke();
    }

    if (dataPoints.length > 1) {
      for (let i = 0; i < dataPoints.length - 1; i++) {
        const x1 = (canvas.width / 2) - (dataPoints.length - 1 - i) * 10;
        const y1 = canvas.height - (dataPoints[i] / 150) * canvas.height;
        const x2 = (canvas.width / 2) - (dataPoints.length - 2 - i) * 10;
        const y2 = canvas.height - (dataPoints[i + 1] / 150) * canvas.height;

        const color = fixedValue !== null ? (dataPoints[i + 1] > fixedValue ? 'green' : 'red') : '#FFD700'; // Цвет линий
        ctx.strokeStyle = color;
        ctx.lineWidth = 3; // Толщина линий
        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.stroke();
        ctx.closePath();
      }

      // Рисуем фиксированную горизонтальную линию
      if (fixedValue !== null) {
        const fixedY = canvas.height - (fixedValue / 150) * canvas.height;
        ctx.strokeStyle = 'lightblue';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(30, fixedY);
        ctx.lineTo(canvas.width, fixedY);
        ctx.stroke();
      }
    }
  }, [dataPoints, fixedValue]);

  const handleFixValue = () => {
    setFixedValue(values[currentIndex - 1]);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        width={500}
        height={300}
        style={{ border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button 
        onClick={handleFixValue} 
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
