import React, { useEffect, useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import './LevelPage.css';

const cards = [
  { id: 1, title: 'Card 1', content: 'Content for card 1' },
  { id: 2, title: 'Card 2', content: 'Content for card 2' },
  { id: 3, title: 'Card 3', content: 'Content for card 3' }
];

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [activeCard, setActiveCard] = useState(0);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  // Обработчики свайпов
  const handlers = useSwipeable({
    onSwipedLeft: () => setActiveCard((prev) => (prev + 1) % cards.length),
    onSwipedRight: () => setActiveCard((prev) => (prev - 1 + cards.length) % cards.length),
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <div className="level-stars"></div>
        <h1>Level Page</h1>

        {/* Карусель с карточками */}
        <div className="carousel-container" {...handlers}>
          {cards.map((card, index) => (
            <div
              key={card.id}
              className={`carousel-card ${index === activeCard ? 'active' : ''}`}
              style={{ transform: `translateX(${(index - activeCard) * 100}%)` }}
            >
              <div className="card-content">
                <h2>{card.title}</h2>
                <p>{card.content}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Индикатор карточек */}
        <div className="carousel-indicators">
          {cards.map((_, index) => (
            <span key={index} className={`indicator ${index === activeCard ? 'active' : ''}`}></span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LevelPage;
