import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const Bubble = ({ size, style, onClick }) => {
  const bubbleStyle = {
    width: size,
    height: size,
    borderRadius: '50%',
    backgroundColor: 'skyblue',
    position: 'absolute',
    ...style,
  };

  return <animated.div style={bubbleStyle} onClick={onClick} />;
};

const Bubbles = () => {
  const [bubbles, setBubbles] = useState([
    { id: 1, size: 50, x: 100, y: 100 },
    { id: 2, size: 70, x: 200, y: 150 },
    { id: 3, size: 60, x: 300, y: 200 },
    // Добавьте больше пузырьков
  ]);

  const removeBubble = (id) => {
    setBubbles((prevBubbles) => prevBubbles.filter((bubble) => bubble.id !== id));
  };

  const springs = useSpring({
    bubbles,
    config: { tension: 180, friction: 12 },
  });

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      {bubbles.map((bubble, index) => (
        <Bubble
          key={bubble.id}
          size={bubble.size}
          style={{ left: springs.bubbles[index]?.x, top: springs.bubbles[index]?.y }}
          onClick={() => removeBubble(bubble.id)}
        />
      ))}
    </div>
  );
};

export default Bubbles;
