import React, { useEffect, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [cards, setCards] = useState([
    { id: 1, content: 'Card 1' },
    { id: 2, content: 'Card 2' },
    { id: 3, content: 'Card 3' },
  ]);

  // Анимация на загрузку
  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const addCard = () => {
    const newCard = { id: cards.length + 1, content: `Card ${cards.length + 1}` };
    setCards([...cards, newCard]);
  };

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <div className="level-stars"></div>
        <h1>1 Level</h1>
        <button onClick={addCard}> Add Card </button>
      </div>
      <div className="level-content">
        <Swiper
          spaceBetween={30} // Увеличиваем отступы между карточками
          slidesPerView={1.5} // Показываем больше соседних карточек
          centeredSlides={true} // Центрируем активную карточку
          loop={true} // Зацикливаем прокрутку
        >
          {cards.map(card => (
            <SwiperSlide key={card.id}>
              <div className="card">{card.content}</div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

export default LevelPage;
