import React, { useEffect, useState } from 'react';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [level, setLevel] = useState(1);
  const [points, setPoints] = useState(0);

  const levelColors = [
    'rgba(0, 255, 255, 0.5)', // Уровень 1 - неоново-синий
    'rgba(0, 255, 0, 0.5)',   // Уровень 2 - неоново-зеленый
    'rgba(255, 255, 0, 0.5)', // Уровень 3 - неоново-желтый
    'rgba(255, 165, 0, 0.5)', // Уровень 4 - неоново-оранжевый
    'rgba(255, 0, 0, 0.5)',   // Уровень 5 - неоново-красный
    'rgba(255, 0, 255, 0.5)', // Уровень 6 - неоново-фиолетовый
    'rgba(0, 0, 255, 0.5)',   // Уровень 7 - неоново-синий темный
    'rgba(75, 0, 130, 0.5)',  // Уровень 8 - индиго
    'rgba(238, 130, 238, 0.5)', // Уровень 9 - неоново-розовый
    'rgba(0, 128, 128, 0.5)', // Уровень 10 - бирюзовый
  ];

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const handleAddPoint = () => {
    if (points < 5) {
      setPoints(points + 1);
    }
  };

  const handleLevelUp = () => {
    if (points >= 5) {
      setLevel(level + 1);
      setPoints(0); // Обнуляем очки после повышения уровня
    } else {
      alert("Нужно 5 поинтов для получения следующего уровня!");
    }
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      <div style={{
        width: '100%',
        height: headerExpand ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>
        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px'
        }}>{level} LEVEL</h1>
      </div>

      <div style={{
        marginTop: '40px',
        width: '60%',
        height: '30px',
        borderRadius: '10px',
        border: '1px solid black',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto'
      }}>
        {Array.from({ length: 5 }).map((_, index) => (
          <div key={index} style={{
            width: '19%',
            height: '100%',
            borderRadius: '10px',
            margin: '0 1%',
            backgroundColor: points > index ? levelColors[level - 1].replace('0.5', '1') : levelColors[level - 1],
            transition: 'background-color 0.3s ease'
          }}></div>
        ))}
      </div>

      <button onClick={handleAddPoint} style={{
        marginTop: '20px',
        padding: '10px 20px',
        fontSize: '16px',
        borderRadius: '5px',
        cursor: 'pointer',
        backgroundColor: '#333',
        color: '#fff',
        border: 'none'
      }}>
        +1 Point
      </button>

      {points === 5 && (
        <button onClick={handleLevelUp} style={{
          marginTop: '20px',
          padding: '10px 20px',
          fontSize: '16px',
          borderRadius: '5px',
          cursor: 'pointer',
          backgroundColor: '#ff5733',
          color: '#fff',
          border: 'none'
        }}>
          Получить уровень {level + 1}
        </button>
      )}

      {/* Добавление анимации через тег style */}
      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
