import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const Bubble = ({ size, style, onClick }) => {
  const bubbleStyle = {
    width: size,
    height: size,
    borderRadius: '50%',
    backgroundColor: 'skyblue',
    position: 'absolute',
    margin: '5px', // Добавляем маргин
    ...style,
  };

  return <animated.div style={bubbleStyle} onClick={onClick} />;
};

const Bubbles = () => {
  const [bubbles, setBubbles] = useState([
    { id: 1, size: 50, x: -60, y: -60 },
    { id: 2, size: 70, x: 80, y: -30 },
    { id: 3, size: 60, x: -100, y: 80 },
    { id: 4, size: 40, x: 0, y: 100 },
    { id: 5, size: 55, x: 100, y: 50 },
  ]);

  const removeBubble = (id) => {
    setBubbles((prevBubbles) => prevBubbles.filter((bubble) => bubble.id !== id));
  };

  const springs = useSpring({
    bubbles,
    config: { tension: 180, friction: 12 },
  });

  return (
    <div
      style={{
        position: 'relative',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {bubbles.map((bubble, index) => (
        <Bubble
          key={bubble.id}
          size={bubble.size}
          style={{
            transform: `translate(${springs.bubbles[index]?.x || bubble.x}px, ${springs.bubbles[index]?.y || bubble.y}px)`,
          }}
          onClick={() => removeBubble(bubble.id)}
        />
      ))}
    </div>
  );
};

export default Bubbles;
