import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  const sliderStyle = {
    width: '80%',
    backgroundColor: 'gray',
    position: 'relative',
    margin: '20px auto'
  };

  const handleStyle = {
    position: 'absolute',
    top: '-15px',
    left: `calc(${value}% - 15px)`,
    width: '30px',
    height: '30px',
    background: 'rgba(255, 255, 255, 0.6)',
    borderRadius: '50%',
    border: '2px solid black',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.5)',
    cursor: 'pointer'
  };

  const sliderTrackStyle = {
    width: '100%',
    height: '5px',
    backgroundColor: 'gray',
    position: 'relative'
  };

  const sliderFillStyle = {
    height: '5px',
    backgroundColor: 'black',
    position: 'absolute',
    left: 0,
    top: 0,
    width: `${value}%`
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div style={{ marginBottom: '10px' }}>
        <span>{value}%</span>
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coins" 
          style={{ width: '30px', marginLeft: '10px' }} 
        />
      </div>
      <div style={sliderStyle}>
        <div style={sliderTrackStyle}>
          <div style={sliderFillStyle}></div>
        </div>
        <input
          type="range"
          min="0"
          max="100"
          value={value}
          onChange={handleChange}
          style={{ width: '100%', appearance: 'none', background: 'transparent', position: 'relative' }}
        />
        <div style={handleStyle}></div>
      </div>
    </div>
  );
};

export default PercentageSlider;
