import React, { useEffect, useState } from 'react';
import { useSprings, animated } from 'react-spring';
import './LevelPage.css';

const generatePositions = (bubblesCount) => {
  const radius = 100;
  const positions = [];
  const angleStep = (2 * Math.PI) / (bubblesCount - 1); // Угол между каждым пузырьком

  // Центр
  positions.push([0, 0]);

  // Остальные пузырьки располагаются по кругу
  for (let i = 1; i < bubblesCount; i++) {
    const angle = i * angleStep;
    const x = radius * Math.cos(angle);
    const y = radius * Math.sin(angle);
    positions.push([x, y]);
  }
  return positions;
};

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [bubbles, setBubbles] = useState([0, 1, 2, 3, 4, 5, 6, 7]);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const handleBubbleClick = (index) => {
    setBubbles(bubbles.filter((bubble) => bubble !== index));
  };

  const positions = generatePositions(bubbles.length);

  const springs = useSprings(
    bubbles.length,
    bubbles.map((bubble, i) => ({
      from: { transform: 'scale(1) translate(0px, 0px)' },
      to: {
        transform: `scale(1) translate(${positions[i][0]}px, ${positions[i][1]}px)`,
      },
      config: { tension: 220, friction: 20 },
    }))
  );

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <h1 className="level-title">Level Page</h1>
        <div className="bubble-container">
          {springs.map((springStyles, i) => (
            <animated.div
              key={bubbles[i]}
              className="bubble"
              style={springStyles}
              onClick={() => handleBubbleClick(bubbles[i])}
            >
              {bubbles[i] + 1}
            </animated.div>
          ))}
        </div>
      </div>
      <div className="level-content"></div>
    </div>
  );
};

export default LevelPage;
