import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', position: 'relative' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
            position: 'absolute',
            top: '50%',
            transform: 'translateY(-50%)',
          }}
        />
      </div>
      <div style={{ position: 'relative', width: '100%', height: '40px', display: 'flex', alignItems: 'center' }}>
        <div style={{ width: '100%', height: '4px', backgroundColor: '#ccc', position: 'absolute', top: '50%', transform: 'translateY(-50%)' }}></div>
        <input 
          type="range" 
          min="0" 
          max="100" 
          value={value} 
          onChange={handleChange} 
          style={{ 
            width: '100%', 
            position: 'relative',
            background: 'transparent',
            appearance: 'none',
            outline: 'none',
          }} 
        />
        <div style={{ display: 'flex', justifyContent: 'space-between', position: 'absolute', width: '100%' }}>
          {[0, 25, 50, 75, 100].map((point) => (
            <div
              key={point}
              style={{
                width: '14px',
                height: '14px',
                backgroundColor: point === Number(value) ? '#000' : '#fff',
                border: '2px solid #000',
                borderRadius: '50%',
                boxSizing: 'border-box',
              }}
            ></div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PercentageSlider;
