import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const canvasRef = useRef(null);
    const [isRunning, setIsRunning] = useState(false);
    const [rectangles, setRectangles] = useState([]);
    const [currentRect, setCurrentRect] = useState(null);

    const canvasWidth = 600;  // Ширина canvas
    const canvasHeight = 400; // Высота canvas
    const rectWidth = 30;     // Ширина каждого прямоугольника
    const centerY = canvasHeight / 2;  // Центр canvas по оси Y
    const centerX = canvasWidth / 2;   // Центр canvas по оси X

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvasWidth, canvasHeight);  // Очистка canvas

        // Рисуем уже существующие прямоугольники
        rectangles.forEach(rect => {
            ctx.beginPath();
            ctx.rect(centerX + rect.xOffset, centerY - rect.yOffset, rectWidth, rect.height);
            ctx.strokeStyle = rect.direction > 0 ? 'green' : 'red';
            ctx.lineWidth = 2; // Толщина линии
            ctx.stroke();
            ctx.closePath();
        });

        // Рисуем текущий прямоугольник в реальном времени
        if (currentRect) {
            ctx.beginPath();
            ctx.rect(centerX + currentRect.xOffset, centerY - currentRect.yOffset, rectWidth, currentRect.height);
            ctx.strokeStyle = currentRect.direction > 0 ? 'green' : 'red';
            ctx.lineWidth = 2; // Толщина линии
            ctx.stroke();
            ctx.closePath();
        }
    }, [rectangles, currentRect]);

    const animateRectangle = () => {
        const newTarget = Math.floor(Math.random() * 41) * (Math.random() > 0.5 ? 1 : -1);
        const stepHeight = newTarget / 100; // 10 секунд, 100 шагов (по 100мс каждый)
        const direction = newTarget > 0 ? 1 : -1;
        let currentHeight = 0;
        let currentYOffset = rectangles.length > 0 ? rectangles[rectangles.length - 1].yOffset : 0; // Начальное смещение от предыдущего прямоугольника

        setCurrentRect({ height: 0, direction, xOffset: 0, yOffset: currentYOffset });

        const interval = setInterval(() => {
            currentHeight += stepHeight;
            currentYOffset += stepHeight * direction;

            setCurrentRect({
                height: Math.abs(currentHeight),
                direction,
                xOffset: -rectangles.length * rectWidth,
                yOffset: currentYOffset
            });

            if (Math.abs(currentHeight) >= Math.abs(newTarget)) {
                clearInterval(interval);
                setRectangles(prevRectangles => [
                    ...prevRectangles,
                    {
                        height: Math.abs(currentHeight),
                        direction,
                        xOffset: -(prevRectangles.length + 1) * rectWidth,
                        yOffset: currentYOffset
                    }
                ]);
                setCurrentRect(null);
            }
        }, 100);
    };

    useEffect(() => {
        if (!isRunning) return;

        const interval = setInterval(animateRectangle, 10000);
        animateRectangle(); // Запуск анимации сразу при первом запуске

        return () => clearInterval(interval);
    }, [isRunning]);

    const handleStartStop = () => {
        setIsRunning(prevState => !prevState);
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div>
                <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} style={{ border: '1px solid black' }} />
                <button onClick={handleStartStop} style={{ display: 'block', margin: '10px auto' }}>
                    {isRunning ? 'Stop' : 'Start'}
                </button>
            </div>
        </div>
    );
};

export default Page4;
