import React, { useState, useEffect } from 'react';

const Page4 = () => {
    const [dimensions, setDimensions] = useState({ width: 100, height: 100 });
    const [intervalId, setIntervalId] = useState(null);

    const getRandomValue = () => {
        return Math.floor(Math.random() * 61) - 30;
    };

    const startGrowing = () => {
        if (!intervalId) {
            const id = setInterval(() => {
                setDimensions(prevDimensions => ({
                    width: prevDimensions.width + 1,
                    height: prevDimensions.height + 1,
                }));
            }, 100);

            setIntervalId(id);
        }
    };

    useEffect(() => {
        if (dimensions.width > 100 && dimensions.height > 100) {
            clearInterval(intervalId);
            setIntervalId(null);
        }
    }, [dimensions, intervalId]);

    return (
        <div>
            <button onClick={startGrowing}>Start Growing</button>
            <div
                style={{
                    width: `${dimensions.width}px`,
                    height: `${dimensions.height}px`,
                    backgroundColor: 'lightblue',
                    marginTop: getRandomValue(),
                    marginLeft: getRandomValue(),
                }}
            ></div>
        </div>
    );
};

export default Page4;
