import React, { useState, useEffect, useRef } from 'react';
import './Page3.css';
import ConfettiCanvas from './ConfettiCanvas';
import LoadingScreen from './LoadingScreen';
import { useNavigate } from 'react-router-dom';

function Page3() {
  const [value, setValue] = useState(0.00);
  const [balance, setBalance] = useState(0.00);
  const [bet, setBet] = useState('');
  const [tickets, setTickets] = useState(9);
  const [message, setMessage] = useState('Введите ставку');
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [result, setResult] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [animateInput, setAnimateInput] = useState(false);
  const [animateValue, setAnimateValue] = useState(false);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState('');

  const intervalRef = useRef(null);
  const timeoutRef = useRef(null);
  const navigate = useNavigate(); // Хук для навигации

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        navigate('/page1'); // Переход на Page1 при нажатии на кнопку назад
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    const checkTicketsReset = async () => {
      const now = new Date();
      const lastReset = await getCloudItem('lastReset');
      const lastResetDate = lastReset ? new Date(lastReset) : null;

      if (!lastResetDate || now.getDate() !== lastResetDate.getDate()) {
        setTickets(9);
        setCloudItem('tickets', '9');
        setCloudItem('lastReset', now.toString());
      } else {
        const savedTickets = await getCloudItem('tickets');
        setTickets(savedTickets ? parseInt(savedTickets, 10) : 9);
      }
    };

    const loadData = async () => {
      const savedBalance = await getCloudItem('balance');
      setBalance(savedBalance ? parseFloat(savedBalance) : 0.00);
      await checkTicketsReset();
      setLoading(false);
    };

    loadData();
    const interval = setInterval(checkTicketsReset, 60000); // Проверка каждую минуту

    return () => clearInterval(interval);
  }, []);

  const setCloudItem = (key, value) => {
    return new Promise((resolve, reject) => {
      window.Telegram.WebApp.CloudStorage.setItem(key, value, (error, result) => {
        if (error) {
          console.error('Error setting item:', error);
          reject(error);
        } else {
          resolve(result);
        }
      });
    });
  };

  const getCloudItem = (key) => {
    return new Promise((resolve, reject) => {
      window.Telegram.WebApp.CloudStorage.getItem(key, (error, result) => {
        if (error) {
          console.error('Error getting item:', error);
          reject(error);
        } else {
          resolve(result);
        }
      });
    });
  };

  const handle10 = (cef) => {
    const betValue = Math.floor(balance * cef);
    setBet(betValue);
  }

  const handleButtonPress = () => {
    const betAmount = parseFloat(bet);
    if (!isNaN(betAmount) && betAmount <= balance && betAmount > 0 && tickets > 0 && !isPlaying) {
      setIsPlaying(true);
      setValue(1.00);
      setGameOver(false);
      setResult(null);
      setShowConfetti(false);
      setTickets((prevTickets) => {
        const newTickets = prevTickets - 1;
        setCloudItem('tickets', newTickets.toString());
        return newTickets;
      });
      setBalance((prevBalance) => {
        const newBalance = parseFloat((prevBalance - betAmount).toFixed(2));
        setCloudItem('balance', newBalance.toString());
        return isNaN(newBalance) || newBalance < 0 ? 0 : newBalance;
      });

      const maxMultiplier = 20.00;
      intervalRef.current = setInterval(() => {
        setValue((prevValue) => parseFloat((prevValue + 0.01).toFixed(2)));
        triggerHapticFeedback();
      }, 100); // Увеличиваем коэффициент каждые 100 мс

      const randomFactor = Math.random();
      let randomTime;

      if (randomFactor < 0.1) {
        // 10% случаев - от 0 до 1000
        randomTime = Math.floor(Math.random() * 1001);
      } else if (randomFactor < 0.2) {
        // Следующие 10% - от 1100 до 2000
        randomTime = Math.floor(Math.random() * 901) + 1100;
      } else if (randomFactor < 0.3) {
        // Следующие 10% - от 2100 до 3000
        randomTime = Math.floor(Math.random() * 901) + 2100;
      } else if (randomFactor < 0.4) {
        // Следующие 10% - от 3100 до 4000
        randomTime = Math.floor(Math.random() * 901) + 30100;
      } else if (randomFactor < 0.5) {
        // Следующие 10% - от 4100 до 5000
        randomTime = Math.floor(Math.random() * 901) + 4100;
      } else if (randomFactor < 0.6) {
        // Следующие 10% - от 5100 до 6000
        randomTime = Math.floor(Math.random() * 901) + 55100;
      } else if (randomFactor < 0.7) {
        // Следующие 10% - от 6100 до 7000
        randomTime = Math.floor(Math.random() * 901) + 66100;
      } else if (randomFactor < 0.8) {
        // Следующие 10% - от 7100 до 8000
        randomTime = Math.floor(Math.random() * 901) + 7100;
      } else if (randomFactor < 0.9) {
        // Следующие 10% - от 8100 до 9000
        randomTime = Math.floor(Math.random() * 901) + 28100;
      } else {
        // Последние 10% - от 9100 до 10000
        randomTime = Math.floor(Math.random() * 901) + 99100;
      }




      timeoutRef.current = setTimeout(() => {
        clearInterval(intervalRef.current);
        setGameOver(true);
        setResult(`-${betAmount.toFixed(2)} coin`);
        setIsPlaying(false);
        setBet(''); // Сбрасываем ставку
        triggerHapticFeedbackError();
        setTimeout(() => {
          setGameOver(false);
          setResult(null);

        }, 1000); // 1 секунда задержки
      }, randomTime);
    }
  };

  const handleButtonRelease = () => {
    if (!gameOver && isPlaying) {
      clearInterval(intervalRef.current);
      clearTimeout(timeoutRef.current);
      const betAmount = parseFloat(bet);
      const winnings = parseFloat((betAmount * value).toFixed(2));
      const newBalance = parseFloat((balance + winnings).toFixed(2));
      setBalance(isNaN(newBalance) || newBalance < 0 ? 0 : newBalance);
      setCloudItem('balance', (isNaN(newBalance) ? 0 : newBalance).toString());
      setResult(`+${winnings.toFixed(2)} coin`);
      setBet('');
      setValue(0.00);
      setIsPlaying(false);
      setShowConfetti(true);
      triggerHapticFeedbackSuccess();

      setTimeout(() => {
        setGameOver(false);
        setResult(null);
      }, 1000);
    }
  };

  if (loading) {
    return <LoadingScreen onEnd={() => setLoading(false)} />;
  }

  return (
    <div className="page3">
      <div className="ticket-display">
        {tickets ? tickets : timeLeft} 🎫
      </div>
      <div className={`balance-display ${balance ? 'slide-in-right' : ''}`}>
        <img src="https://kairosrainbow.it/images/coin.png" alt="Coin" className="coin-image" />
        {balance.toFixed(2)}
      </div>
      <div className={`input-container ${animateInput ? 'animate' : ''}`}>
        <input
          type="text"
          className="number-input"
          placeholder="0"
          value={bet}
          onChange={(e) => setBet(e.target.value)}
          onFocus={() => setAnimateInput(false)}
        />
        <div className={`animated-value ${animateValue ? 'animate' : ''}`}>{bet}</div>
      </div>
      <div className="buttons-container">
        <button className="button-10" onClick={() => handle10(0.1)}>10%</button>
        <button className="button-10" onClick={() => handle10(0.5)}>50%</button>
        <button className="button-10" onClick={() => handle10(1)}>100%</button>
      </div>
      <div
        className={`value-display ${isPlaying ? 'playing' : ''} ${gameOver ? 'game-over' : ''}`}
        onMouseDown={handleButtonPress}
        onMouseUp={handleButtonRelease}
        onTouchStart={handleButtonPress}
        onTouchEnd={handleButtonRelease}
      >
        {value.toFixed(2)}
      </div>
      {gameOver && <div className="game-over-message">{result}</div>}
      {showConfetti && <ConfettiCanvas />}
    </div>
  );
}

export default Page3;
