import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const [centerValue, setCenterValue] = useState(null);
    const [previousValue, setPreviousValue] = useState(null);
    const [currentValue, setCurrentValue] = useState(null);
    const [rectangles, setRectangles] = useState([]);
    const [lastY, setLastY] = useState(null);
    const [updatedValue, setUpdatedValue] = useState(null);  // Новый стейт для обновляемого значения
    const canvasRef = useRef(null);
    const animationTimeoutRef = useRef(null);

    const fetchBitcoinPrice = async () => {
        try {
            const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
            const data = await response.json();
            const price = parseFloat(data.bpi.USD.rate.replace(',', ''));
            return price;
        } catch (error) {
            console.error('Ошибка при получении цены биткоина:', error);
            return 56000;
        }
    };

    const generateValue = () => {
        if (centerValue !== null) {
            const randomRangeSelector = Math.random();
            let newValue;

            if (randomRangeSelector <= 0.6) {
                const min = centerValue - 345;
                const max = centerValue + 345;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            } else if (randomRangeSelector <= 0.8) {
                const min = centerValue - 500;
                const max = centerValue + 500;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            } else {
                const min = centerValue - 1990;
                const max = centerValue + 1990;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            }

            setCurrentValue(newValue);
        }
    };

    const generateRandomMultiplier = () => {
        const rangeSelector = Math.random();

        if (rangeSelector <= 0.6) {
            return Math.random() * (0.1 - 0.01) + 0.01;
        } else if (rangeSelector <= 0.8) {
            return Math.random() * (0.5 - 0.11) + 0.11;
        } else if (rangeSelector <= 0.9) {
            return Math.random() * (0.9 - 0.51) + 0.51;
        } else {
            return Math.random() * (1 - 0.91) + 0.91;
        }
    };

    useEffect(() => {
        const initialize = async () => {
            const price = await fetchBitcoinPrice();
            setCenterValue(price);
            setPreviousValue(price);
            setCurrentValue(price);
            setUpdatedValue(price);  // Инициализация нового стейта
        };

        initialize();
    }, []);

    useEffect(() => {
        if (currentValue !== previousValue && previousValue !== null) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');

            const scale = window.devicePixelRatio || 1;
            canvas.width = 500 * scale;
            canvas.height = 300 * scale;
            ctx.scale(scale, scale);

            const difference = currentValue - previousValue;
            let step = difference > 0 ? 1 : -1;
            let progress = 0;

            const startY = lastY !== null ? lastY : 150 - ((previousValue - centerValue) / 1990) * 120;
            const targetY = 150 - ((currentValue - centerValue) / 1990) * 120;
            const rectWidth = 20;

            let animationDirection = 1;
            const startTime = Date.now();

            const interval = setInterval(() => {
                const randomMultiplier = generateRandomMultiplier();
                progress += step * animationDirection * randomMultiplier;
                setUpdatedValue(prev => prev + randomMultiplier);  // Обновляем значение

                ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

                const updatedRectangles = rectangles.map((rect) => ({
                    ...rect,
                    x: rect.x - rectWidth,
                }));

                updatedRectangles.forEach((rect) => {
                    ctx.fillStyle = rect.color;
                    ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
                });

                const height = Math.abs(progress);
                let rectY;

                if (difference > 0) {
                    rectY = startY - height;
                } else {
                    rectY = startY;
                }

                ctx.fillStyle = difference > 0 ? 'green' : 'red';
                ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

                if (Math.abs(progress) >= Math.abs(targetY - startY)) {
                    animationDirection *= -1;
                }

                if (Date.now() - startTime >= 5000) { 
                    clearInterval(interval);

                    updatedRectangles.push({
                        x: canvas.width / scale / 2 - 15,
                        y: rectY,
                        width: rectWidth,
                        height: height,
                        color: difference > 0 ? 'green' : 'red',
                    });

                    setRectangles(updatedRectangles);
                    setLastY(difference > 0 ? rectY : rectY + height);
                    setPreviousValue(currentValue);

                    generateValue();
                }
            }, 20);

            animationTimeoutRef.current = interval;

            return () => clearInterval(interval);
        }
    }, [currentValue, previousValue, centerValue, rectangles, lastY]);

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <canvas
                ref={canvasRef}
                style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
            ></canvas>
            <div>
                Current Value: {currentValue}
            </div>
            <div>
                Updated Value: {updatedValue.toFixed(2)}  {/* Выводим значение с двумя знаками после запятой */}
            </div>
        </div>
    );
};

export default Page4;
