import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [currentValue, setCurrentValue] = useState(0);
  const [targetValue, setTargetValue] = useState(0); // Целевая цена, к которой двигается текущая
  const [dataPoints, setDataPoints] = useState([]);
  const [centerValue, setCenterValue] = useState(0); // Центр шкалы
  const [range, setRange] = useState(1000); // Полный диапазон шкалы (±200)
  const canvasRef = useRef(null);

  // Функция для получения цены биткоина
  const fetchBitcoinPrice = async () => {
    try {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
      const data = await response.json();
      const price = data.bpi.USD.rate_float;
      setCurrentValue(initialPrice);
      return Math.round(price);
    } catch (error) {
      console.error('Ошибка при получении цены биткоина:', error);
      return currentValue; // Возвращаем текущее значение при ошибке
    }
  };

  // Функция для генерации случайного изменения цены
  const getRandomPriceChange = () => {
    const change = Math.floor(Math.random() * (250 - 50 + 1)) + 50;
    return Math.random() > 0.5 ? change : -change;
  };

  useEffect(() => {
    const updatePrice = async () => {
      const initialPrice = await fetchBitcoinPrice();
      setCurrentValue(initialPrice);
      setCenterValue(initialPrice); // Изначально центр шкалы равен текущей цене
      setTargetValue(initialPrice);
    };

    updatePrice();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentValue(prevValue => {
        if (prevValue < targetValue) {
          return prevValue + 1;
        } else if (prevValue > targetValue) {
          return prevValue - 1;
        } else {
          setDataPoints(prev => [...prev, prevValue]);
          // Обновляем цель, только когда текущее значение достигает целевого значения
          setTargetValue(currentValue + getRandomPriceChange());
          return prevValue;
        }
      });
    }, 10); // Интервал для плавного изменения значения

    return () => clearInterval(interval);
  }, [currentValue, targetValue]);

  useEffect(() => {
    if (currentValue < centerValue - range / 2 || currentValue > centerValue + range / 2) {
      // Обновляем центр шкалы, если текущее значение выходит за границы диапазона
      setCenterValue(currentValue);
    }
  }, [currentValue, centerValue, range]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 300 * scale;
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      ctx.strokeStyle = '#FFD700';
      ctx.lineWidth = 1.5;
      ctx.beginPath();

      dataPoints.forEach((value, index) => {
        const x = (canvas.width / scale / 2) + index * 10 - dataPoints.length * 10;
        const y = canvas.height / scale - ((value - (centerValue - range / 2)) / range) * canvas.height / scale;
        if (index === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      const fixedY = canvas.height / scale - ((currentValue - (centerValue - range / 2)) / range) * canvas.height / scale;
      ctx.strokeStyle = 'lightblue';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, fixedY);
      ctx.lineTo(canvas.width / scale, fixedY);
      ctx.stroke();

      ctx.font = '12px Arial';
      ctx.fillStyle = 'lightblue';
      ctx.fillText(`${currentValue.toString()}`, canvas.width / scale - 150, fixedY - 10);
    }
  }, [dataPoints, centerValue, range, currentValue]);

  const handleFixValue = () => {
    setCenterValue(currentValue); // Перемещаем центр шкалы на текущее значение
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
