import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const [centerValue, setCenterValue] = useState(null);
    const [previousValue, setPreviousValue] = useState(null);
    const [currentValue, setCurrentValue] = useState(null);
    const [currentValueShow, setCurrentValueShow] = useState(1);
    const [rectangles, setRectangles] = useState([]);
    const [lastY, setLastY] = useState(null);
    const canvasRef = useRef(null);
    const lineCanvasRef = useRef(null);
    const animationTimeoutRef = useRef(null);

    // State for the independent line
    const [independentLineY, setIndependentLineY] = useState(null);
    const [isIndependentLineVisible, setIsIndependentLineVisible] = useState(false);

    const fetchBitcoinPrice = async () => {
        try {
            const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
            const data = await response.json();
            const price = parseFloat(data.bpi.USD.rate.replace(',', ''));
            return price;
        } catch (error) {
            console.error('Ошибка при получении цены биткоина:', error);
            return 56000;
        }
    };

    const generateValue = () => {
        if (centerValue !== null) {
            const randomRangeSelector = Math.random();
            let newValue;

            if (randomRangeSelector <= 0.6) {
                const min = centerValue - 222;
                const max = centerValue + 222;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            } else if (randomRangeSelector <= 0.8) {
                const min = centerValue - 333;
                const max = centerValue + 333;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            } else {
                const min = centerValue - 1111;
                const max = centerValue + 1111;
                newValue = Math.floor(Math.random() * (max - min + 1)) + min;
            }

            setCurrentValue(newValue);
        }
    };

    const generateRandomMultiplier = () => {
        const rangeSelector = Math.random();

        if (rangeSelector <= 0.4) {
            return Math.random() * (0.1 - 0.01) + 0.01;
        } else if (rangeSelector <= 0.7) {
            return Math.random() * (0.5 - 0.11) + 0.11;
        } else if (rangeSelector <= 0.8) {
            return Math.random() * (0.9 - 0.51) + 0.51;
        } else {
            return Math.random() * (1 - 0.91) + 0.91;
        }
    };

    useEffect(() => {
        const initialize = async () => {
            const price = await fetchBitcoinPrice();
            setCenterValue(price);
            setPreviousValue(price);
            setCurrentValue(price);
            setCurrentValueShow(price);
        };

        initialize();
    }, []);

    useEffect(() => {
        if (centerValue !== null) {
            generateValue();
        }
    }, [centerValue]);

    useEffect(() => {
        const startDrawing = () => {
            const drawRectangle = () => {
                if (currentValue !== previousValue && previousValue !== null) {
                    const canvas = canvasRef.current;
                    const ctx = canvas.getContext('2d');

                    const difference = currentValue - previousValue;
                    let step = difference > 0 ? 1 : -1;
                    let progress = 0;

                    const startY = lastY !== null ? lastY : 150 - ((previousValue - centerValue) / 1111) * 120;
                    const targetY = 150 - ((currentValue - centerValue) / 1111) * 120;
                    const rectWidth = 20;

                    let animationDirection = 1;
                    let hasReachedTarget = false; // Флаг для отслеживания достижения targetY
                    const startTime = Date.now();

                    const interval = setInterval(() => {
                        const randomMultiplier = generateRandomMultiplier();
                        progress += step * animationDirection * randomMultiplier;

                        ctx.clearRect(0, 0, canvas.width, canvas.height);

                        const updatedRectangles = rectangles.map((rect) => ({
                            ...rect,
                            x: rect.x - rectWidth,
                        }));

                        updatedRectangles.forEach((rect) => {
                            ctx.fillStyle = rect.color;
                            ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
                        });

                        const height = Math.abs(progress);
                        let rectY;

                        if (difference > 0) {
                            rectY = startY - height;
                        } else {
                            rectY = startY;
                        }

                        ctx.fillStyle = difference > 0 ? 'green' : 'red';
                        ctx.fillRect(canvas.width / 2 - 15, rectY, rectWidth, height);

                        const updatedCurrentValueShow = currentValueShow + progress;
                        const text = updatedCurrentValueShow.toFixed(2);
                        const textX = canvas.width * 0.775 + 5;
                        const textY = difference > 0 ? rectY + 3 : rectY + height + 3;

                        ctx.font = '10px Arial';
                        ctx.fillStyle = 'white';
                        ctx.fillText(text, textX, textY);

                        if (textX + ctx.measureText(text).width > canvas.width) {
                            ctx.clearRect(canvas.width - 60, textY - 10, 60, 15);
                            ctx.fillText(text, canvas.width - 60, textY);
                        }

                        // Переключение направления анимации
                        if (!hasReachedTarget && Math.abs(progress) >= Math.abs(targetY - startY)) {
                            animationDirection *= -1;
                            hasReachedTarget = true;
                        } else if (hasReachedTarget && Math.abs(progress) <= 2) { // Задать предел для почти startY
                            animationDirection *= -1;
                            hasReachedTarget = false;
                        }

                        if (Date.now() - startTime >= 5000) {
                            clearInterval(interval);

                            updatedRectangles.push({
                                x: canvas.width / 2 - 15,
                                y: rectY,
                                width: rectWidth,
                                height: height,
                                color: difference > 0 ? 'green' : 'red',
                            });

                            setRectangles(updatedRectangles);
                            setLastY(difference > 0 ? rectY : rectY + height);
                            setPreviousValue(currentValue);
                            setCurrentValueShow(updatedCurrentValueShow);

                            generateValue();
                        }
                    }, 40);

                    animationTimeoutRef.current = interval;

                    return () => clearInterval(interval);
                }
            };

            drawRectangle();
        };

        if (currentValue !== previousValue && previousValue !== null) {
            startDrawing();
        }
    }, [currentValue, previousValue, centerValue, rectangles, lastY]);

    const handleIndependentLine = () => {
        const lineCanvas = lineCanvasRef.current;
        const lineCtx = lineCanvas.getContext('2d');
        const scale = window.devicePixelRatio || 1;

        const independentLineY = lastY || 150 - ((previousValue - centerValue) / 1111) * 120;
        setIndependentLineY(independentLineY);
        setIsIndependentLineVisible(true);

        lineCtx.strokeStyle = 'yellow';
        lineCtx.lineWidth = 2;
        lineCtx.setLineDash([]);
        lineCtx.beginPath();
        lineCtx.moveTo(0, independentLineY);
        lineCtx.lineTo(lineCanvas.width / scale, independentLineY);
        lineCtx.stroke();

        // Оставляем линию видимой на 30 секунд, не очищая ее
        const timeout = setTimeout(() => {
            setIsIndependentLineVisible(false);
            lineCtx.clearRect(0, 0, lineCanvas.width, lineCanvas.height);
        }, 30000);

        return () => clearTimeout(timeout); // Чистим таймер, если компонент размонтирован
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <canvas
                ref={canvasRef}
                style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
            ></canvas>
            <canvas
                ref={lineCanvasRef}
                style={{ width: 500, height: 300, position: 'absolute', pointerEvents: 'none' }}
            ></canvas>
            <button onClick={handleIndependentLine}>Show Independent Line</button>
        </div>
    );
};

export default Page4;
