import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const Page4 = () => {
  const [balance, setBalance] = useState(0);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentValue, setCurrentValue] = useState(null);
  const [dataPoints, setDataPoints] = useState([]);
  const [centerValue, setCenterValue] = useState(null); // Центр шкалы
  const [range, setRange] = useState(800); // Полный диапазон шкалы (±400)
  const canvasRef = useRef(null);

  useEffect(() => {
    // Получение баланса из CloudStorage
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          setBalance(initialBalance);
        }
      });
    };
    getInitialData();
  }, []);

  useEffect(() => {
    const fetchBitcoinPrice = async () => {
      try {
        const response = await axios.get('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
        const newPrice = parseFloat(response.data.bpi.USD.rate.replace(',', ''));

        if (centerValue === null) {
          setCenterValue(newPrice);
          setCurrentValue(newPrice);
        } else {
          const difference = Math.abs(newPrice - currentValue);
          const intervalDuration = 1500 / difference;

          const interval = setInterval(() => {
            setCurrentValue(prevValue => {
              if (prevValue < newPrice) {
                return prevValue + 1;
              } else if (prevValue > newPrice) {
                return prevValue - 1;
              } else {
                setDataPoints(prev => [...prev, prevValue]);
                clearInterval(interval);
                setCurrentIndex(currentIndex + 1);
                return prevValue;
              }
            });
          }, intervalDuration);
        }
      } catch (error) {
        console.error('Error fetching Bitcoin price:', error);
      }
    };

    fetchBitcoinPrice();
    const interval = setInterval(fetchBitcoinPrice, 3000);

    return () => clearInterval(interval);
  }, [centerValue, currentValue, currentIndex]);

  useEffect(() => {
    if (currentValue < centerValue - range / 2 || currentValue > centerValue + range / 2) {
      // Обновляем центр шкалы, если текущее значение выходит за границы диапазона
      setCenterValue(currentValue);
    }
  }, [currentValue, centerValue, range]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 210 * scale; // Уменьшил высоту на 30%
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      ctx.strokeStyle = '#007bff';
      ctx.lineWidth = 2;
      ctx.beginPath();

      dataPoints.forEach((value, index) => {
        const x = (canvas.width / scale / 2) + index * 10 - dataPoints.length * 10;
        const y = canvas.height / scale - ((value - (centerValue - range / 2)) / range) * canvas.height / scale;
        if (index === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      const fixedY = canvas.height / scale - ((currentValue - (centerValue - range / 2)) / range) * canvas.height / scale;
      ctx.strokeStyle = 'lightblue';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, fixedY);
      ctx.lineTo(canvas.width / scale, fixedY);
      ctx.stroke();

      ctx.font = '12px Arial';
      ctx.fillStyle = 'lightblue';
      ctx.fillText(`${currentValue.toFixed(2)}`, canvas.width / scale - 150, fixedY - 10);
    }
  }, [dataPoints, centerValue, range, currentValue]);

  const handleFixValue = () => {
    setCenterValue(currentValue); // Перемещаем центр шкалы на текущее значение
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh', backgroundColor: '#f5f5f5' }}>
      <div style={{ position: 'absolute', top: '20px', right: '20px', display: 'flex', alignItems: 'center', fontSize: '24px', fontWeight: 'bold' }}>
        <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance Icon" style={{ marginRight: '10px', width: '24px', height: '24px' }} />
        {balance.toLocaleString()}
      </div>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 210, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
