import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [elementsVisible, setElementsVisible] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0);
  const [balance, setBalance] = useState(0);
  const [balanceRate4, setBalanceRate4] = useState(1);
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();

  const balanceThresholds = [5, 10, 20, 40, 80, 160, 320, 640, 1280, 2560, 5120, 10240, 20480, 40960, 81920, 163840];

  const leftTexts = ['Left Text 1', 'Left Text 2', 'Left Text 3'];
  const rightTexts = ['Right Text 1', 'Right Text 2', 'Right Text 3'];

  useEffect(() => {
    // Получаем данные из cloudStorage при загрузке страницы
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate4', 'level4', 'progress4'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialRate = result.balanceRate4 ? parseInt(result.balanceRate4, 10) : 1;
          const initialLevel = result.level4 ? parseInt(result.level4, 10) : 1;
          const initialProgress = result.progress4 ? parseInt(result.progress4, 10) : 0;

          setBalance(initialBalance);
          setBalanceRate4(initialRate);
          setLevel(initialLevel);
          setProgress(initialProgress);
        }
      });
    };

    getInitialData();
  }, []);

  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        setIsExiting(true);
        setTimeout(() => {
          navigate('/');
        }, 850);
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
    setTimeout(() => {
      setElementsVisible(true);
    }, 550);
  }, []);

  const handleIncreaseProgress = () => {
    if (balance >= balanceThresholds[balanceRate4 - 1]) {
      if (progress >= 80) {
        setProgress(prev => prev + 20);
        setTimeout(() => {
          setLevel(prev => prev + 1);
          setProgress(0);
          window.Telegram.WebApp.CloudStorage.setItem('progress4', (0).toString(), (error, success) => {
            if (error) {
              console.error('Error setting progress:', error);
            }
          });
          window.Telegram.WebApp.CloudStorage.setItem('level4', (level + 1).toString(), (error, success) => {
            if (error) {
              console.error('Error setting level:', error);
            }
          });
          setBalanceRate4(prev => prev + 1);
          window.Telegram.WebApp.CloudStorage.setItem('balanceRate4', (balanceRate4 + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
        }, 500);

      } else {
        setProgress(prev => prev + 20);
        window.Telegram.WebApp.CloudStorage.setItem('progress4', (progress + 20).toString(), (error, success) => {
          if (error) {
            console.error('Error setting progress:', error);
          }
        });
        setBalanceRate4(prev => prev + 20 + 1);
          window.Telegram.WebApp.CloudStorage.setItem('balanceRate4', (balanceRate4 + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
      }
    } else {
      alert('Not enough')
    }
  };

  const getProgressColor = () => {
    const colors = [
      '#2196f3',  // Голубой
      '#4caf50',  // Зеленый
      '#b39ddb',  // Светло-фиолетовый
      '#ff9800',  // Оранжевый
      '#ff8a80',  // Светло-красный
      '#cd7f32',  // Бронзовый
      '#c0c0c0',  // Серебристый
      '#ffd700',  // Золотой
      '#e5e4e2',  // Платиновый
      '#b9f2ff'   // Бриллиантовый
    ];

    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          {level} LEVEL
        </h1>

        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          margin: '10px auto',
          backgroundColor: 'rgba(221, 221, 221, 0.1)',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Независимый блок с обработчиком клика для увеличения прогресса */}
        <div onClick={handleIncreaseProgress} style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          cursor: 'pointer',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[0]}</span>
          <span>{rightTexts[0]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[1]}</span>
          <span>{rightTexts[1]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[2]}</span>
          <span>{rightTexts[2]}</span>
        </div>
      </div>

      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
