import React, { useEffect, useState } from 'react';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0); // Прогресс в процентах

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  // Обработчик нажатия на кнопку для увеличения прогресса
  const handleIncreaseProgress = () => {
    setProgress(prev => (prev >= 100 ? 0 : prev + 20)); // Обнуляем прогресс при достижении 100%
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      {/* Верхняя часть */}
      <div style={{
        width: '100%',
        height: headerExpand ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>
        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px'
        }}>{level} LEVEL</h1>
      </div>

      {/* Полоска прогресса */}
      <div style={{
        position: 'relative',
        width: '80%',
        height: '30px',
        margin: '0 auto',
        marginTop: '40px',
        backgroundColor: '#ddd',
        borderRadius: '10px',
        overflow: 'hidden',
      }}>
        <div style={{
          height: '100%',
          width: `${progress}%`,
          backgroundColor: '#4caf50',
          transition: 'width 0.5s ease',
        }}>
          {/* Вертикальные полоски, делящие полоску прогресса на 5 частей */}
          {[...Array(5)].map((_, i) => (
            <div key={i} style={{
              position: 'absolute',
              height: '100%',
              width: '2px',
              backgroundColor: '#fff',
              left: `${(i + 1) * 20}%`,
              top: 0
            }} />
          ))}
        </div>
      </div>

      {/* Кнопка для увеличения прогресса */}
      <button onClick={handleIncreaseProgress} style={{
        marginTop: '20px',
        padding: '10px 20px',
        backgroundColor: '#4caf50',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px'
      }}>
        Increase Progress
      </button>

      {/* Текущий уровень */}
      <p style={{
        marginTop: '20px',
        fontSize: '20px',
        color: '#333',
      }}>Level {level}</p>

      {/* Добавление анимации через тег style */}
      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
